(window.webpackJsonp=window.webpackJsonp||[]).push([[124],{305:function(_,v,t){"use strict";t.r(v);var p=t(7),s=Object(p.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("h1",{attrs:{id:"统计与数学学院"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#统计与数学学院"}},[_._v("#")]),_._v(" 统计与数学学院")]),_._v(" "),v("blockquote",[v("p",[_._v("文/高2012级5班 潘越")])]),_._v(" "),v("p",[v("strong",[_._v("学术氛围：")])]),_._v(" "),v("p",[_._v("学习氛围浓厚,专业课课程多,要求高，特别是各种数学基础课程。学院教授老师经验丰富，教学严谨，善于和同学们展开互动，因而即使上课时存在不懂的地方，下课后也可以即使询问老师，发送邮件或约定见面时间，因为学院整体学术氛围较为浓厚。")]),_._v(" "),v("p",[v("strong",[_._v("活动氛围：")])]),_._v(" "),v("p",[_._v("学院重视各种活动的开展，重视学生的全面发展。无论是运动会，十佳歌手大赛，还是《数韵》征稿，各种大大小小的文艺活动总能看见数院学子忙碌的身影。总的来说，学")]),_._v(" "),v("p",[_._v("院比较重视文体，学生对于各种活动也比较积极。")]),_._v(" "),v("p",[_._v("专业名：数学与应用数学（金融数学方向）")]),_._v(" "),v("p",[_._v("专业介绍：本专业培养掌握数学科学的基本理论与基本方法，具备运用数学知识、使用计算机解决实际问题的能力，受到科学研究的初步训练，能在科技、教育和经济部门从事研究、教学工作或在生产经营及管理部门从事实际应用、开发研究和管理工作的高级专门人才。")]),_._v(" "),v("p",[_._v("培养目标：该专业主要培养在商业银行、金融机构及其他单位从事财务管理、掌握数学科学的基本理论与基本技能，具备运用数学知识和使用计算机技术解决实际问题的能力，受到严格科学思维训练的，能在科技、教育、经济、管理、金融等部门从事实际应用、开发研究和管理工作的高素质应用型人才。")]),_._v(" "),v("p",[_._v("就业方向：各种企业事业单位和金融机构的财务部门；证券交易所、民营产业；各类信金融投资机构；相关高等专业教育和科研单位即各种商业银行等。")]),_._v(" "),v("p",[_._v("专业课详细介绍：")]),_._v(" "),v("p",[_._v("课程名称：数学分析")]),_._v(" "),v("p",[_._v("课程内容：数学分析是数学专业和部分工科专业的必修课程之一，基本内容是以实数理论为基础微积分，但是与微积分有很大的差别。")]),_._v(" "),v("p",[_._v("数学分析的主要内容是微积分学，微积分学的理论基础是极限理论的理论基础是实数理论。实数系最重要的特征是连续性，有了实数的连续性，才能讨论极限，连续，微分和积分。正是在讨论函数的各种极限运算的合法性的过程中，人们逐渐建立起了严密的数学分析理论体系。在内容和体例上未作较大变动。知识内容稍有扩充，涉及的方面很广。增加了少量的说明性文字，使内容更加完善。下册内容包括：级数、多元函数微分学、隐函数、反常积分与含参变量的积分、重积分、曲线积分与曲面积分等。本书阐述细致，范例较多，便于自学，可作为高等师范院校本科教材，也可作为高等理科院校函授教材及高等教育自学用书。")]),_._v(" "),v("p",[_._v("学习目标：数学分析的研究对象是函数，它从局部和整体这两个方面研究函数的基本性态，从而形成微分学和积分学的基本内容。微分学研究变化率等函数的局部特征，导数和微分是它的主要概念，求导数的过程就是微分法。围绕着导数与微分的性质、计算和直接应用，形成微分学的主要内容。积分学则从总体上研究微小变化（尤其是非均匀变化）积累的总效果，其基本概念是原函数（反导数）和定积分，求积分的过程就是积分法。积分的性质、计算、推广与直接应用构成积分学的全部内容。")]),_._v(" "),v("p",[_._v("因此,熟练地运用各种定义,函数与极限解决数学问题是学习数学分析的基本目标。")]),_._v(" "),v("p",[_._v("笔者看法：数学分析是高等数学的基础，唯有学号数学分析，打好数学基础，才能为今后的学习埋下良好的伏笔，提高自身的专业素质。使用教材：数学分析讲义第五版(刘玉琏_傅沛仁_著)高等教育出版社")]),_._v(" "),v("p",[_._v("分数线参考：2012年在640分左右，一般位于全市650名左右。")])])}),[],!1,null,null,null);v.default=s.exports}}]);