(window.webpackJsonp=window.webpackJsonp||[]).push([[208],{390:function(v,_,p){"use strict";p.r(_);var t=p(7),s=Object(t.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("h1",{attrs:{id:"学校综述"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#学校综述"}},[v._v("#")]),v._v(" 学校综述")]),v._v(" "),_("blockquote",[_("p",[v._v("本文于2013年录入/高2012级16班唐若宇")])]),v._v(" "),_("p",[v._v("学校特色：我们学校呢是一所以农业和生命科学为优势的大学。当然也有，理、经、管、工、文、法等众多学院协调发展的教育部直属全国重点大学，是国家“211工程”重点建设大学和“985优势学科创新平台”高校之一")]),v._v(" "),_("p",[v._v("这个学校在南京分两个校区，卫岗校区（就在中山陵下），浦口校区（南京的大农村不解释，只有一个工学院在这边，目前我就在这个校区）。两个校区各有优势，但我人认为本部卫岗校区看起来更像一个大学，因为学院多嘛。但是浦口这边的就业率确实很客观（中国现在大量需要工科人才嘛，但我们学校的工科比起东南大学，南理工，南航确实还是有差距的）不过提早说一点，如果你是娇生惯养的公子小姐的话，我接下来的介绍你就可以不用看了，不要浪费你的时间：学校的宿舍比南开的宿舍不知道差了多少，没空调，没热水器，没电风扇，还有小部分的没独卫。所以如果你担心生活艰苦的话，果断不要报我们学校了。")]),v._v(" "),_("p",[v._v("下面，具体谈谈我们学校的学院，以及我对它们的了解")]),v._v(" "),_("p",[v._v("农学院，这个嘛，农业在全国排第二第三的样子，有兴趣的可以报，不过就业率似乎比较凄惨。")]),v._v(" "),_("p",[v._v("植物保护学院，这是学校的一个优势学院，分植物保护和生态学两个专业。学术氛围还不错，在《NATURE》上也是有重要论文发表。不过这个学院的学生不考研不读博的话，可能工作也是不太好找。")]),v._v(" "),_("p",[v._v("资源环境科学学院，也是学术氛围较浓的一个学院，而且环保这一块前景比较大，就业的形式从目前来说不太乐观。")]),v._v(" "),_("p",[v._v("园艺学院，动物科技学院，动物医学学院我就一起说了，它们其中设置的专业都是学校的比较王牌的学科，但是呢，不出国你的前途确实很难说，特别是园艺，动科。")]),v._v(" "),_("p",[v._v("经济管理学院，虽说学术氛围不太高，但是学金融会计什么的啊，似乎这个专业在中国的大学里都完全普及了。一听到学金融报的人多，分自然比较高，特别是进入大学后还要分班考试，很多人都是冲着这个去的，就业率还不错，汇报也都还成。")]),v._v(" "),_("p",[v._v("公共管理学院，其特色学科是土地资源管理，这个收分相对较高，前景啊，学术氛围都还不错，也是我们学校的重点学科之一。我个人比较看好这个专业，可能唯一的不足还是在就业率上吧。")]),v._v(" "),_("p",[v._v("理学院和生命科学学院都是学术氛围好，其他的我确实不了解。")]),v._v(" "),_("p",[v._v("食品科学技术学院，只是收分较高，有那么一两个优势学科吧。是好是坏也众说纷纭的")]),v._v(" "),_("p",[v._v("什么人文社科学院。信息科学技术学院，外国语学院建议各位学弟学妹们都不要报了，额，应该是比较水的！")]),v._v(" "),_("p",[v._v("工学院，本人就在这个学院里，硬件条件确实不好，学术氛围也没那么浓，但是就业率确实也是在我们学校说得了话的。")]),v._v(" "),_("p",[v._v("下面是自己对于这个大学的一点看法：")]),v._v(" "),_("p",[v._v("才到这边的时候是各种失望啊，感觉什么破学校，什么都没有。但是在这个学校你同样可以享受到其他211大学在机会上给你的汇报。她总归是一个教育部直属的211大学嘛，在就业啊，考研啊出国啊我们的机会都比不是211大学的，或者说211大学里非教育部直属的大学多。并且当你们入学时，我们这所学校的历史就是111年了，在中国是最早的一批大学了，与东南大学同庚的。所以学校文化的积淀还是足足的。")]),v._v(" "),_("p",[v._v("我只所以要向各位学弟学妹们介绍我的这所大学，是因为我想向像我一样的学弟学妹们提供一条路子。那些学长学姐可能给你们介绍的更多的是你们平时有听过但是分数还蛮高的名牌大学，什么浙大啊，人大，复旦，同济啊。这些当时对于我而言是想都没敢想的，我在这实话实说，当时年级立刻1100多人啊，我平时就考个七八百名的样子，算是中下的水品了吧，但现在我觉得以那样的成绩报到这所大学应该，确实应该知足，高中不足的，大学还可以再来赶！")]),v._v(" "),_("p",[v._v("这个学校的收分真心低啊，这几年只要比一本线多个10来分就可以上了.但是像在安徽，河南河北等地这个学校都是要高出重本30——40分才可以读的。")]),v._v(" "),_("p",[v._v("我没有鼓动你们谁填报这所学校，这所学校在重庆名气很小，但是她在中国的某些领域确实是说得起话的。")]),v._v(" "),_("p",[v._v("最后的最后，祝愿各位学弟学妹们能在自己和父母的商量下填上一个令自己满意的无悔的志愿！")])])}),[],!1,null,null,null);_.default=s.exports}}]);