(window.webpackJsonp=window.webpackJsonp||[]).push([[215],{397:function(v,_,p){"use strict";p.r(_);var n=p(7),t=Object(n.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("h1",{attrs:{id:"水利与建筑工程学院"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#水利与建筑工程学院"}},[v._v("#")]),v._v(" 水利与建筑工程学院")]),v._v(" "),_("blockquote",[_("p",[v._v("本文于2013年录入/赵培焱")])]),v._v(" "),_("p",[v._v("水利与建筑工程学院是西北农林科技大学办学历史最悠久的学院之一。1933年春开始招收本科生，1941年开始招收研究生。")]),v._v(" "),_("p",[v._v("学院现设农业水利工程系、水文与水资源工程系、水利水电工程系、土木工程系、动力与电气工程系、材料与结构工程系；有农业水利工程、水文与水资源工程、水利水电工程、土木工程、热能与动力工程、电气及其自动化、城市规划、农业建筑与环境工程等8个本科专业；农业水土工程、水利水电工程、水文与水资源、水力学与河流动力学、水工结构工程、结构工程、岩土工程、土木工程8个硕士学位授权点；农业水土工程、水文与水资源、水利水电工程、农业水资源与水环境工程、水利工程5个博士学位授权点，设有农业工程、水利工程2个博士后流动站。农业水利工程是国家和陕西省重点学科，水利水电工程是陕西省重点学科。农业水利工程专业是国家特色专业点、水文与水资源工程和水利水电工程专业是省级特色建设专业点，农业水利工程专业和水文与水资源工程专业是陕西省名牌专业。学院拥有旱区农业水土工程教育部重点实验室、农业部旱区农业节水工程重点开放实验室、水工程安全与病害防治研究中心、西北水利科学研究所实验中心、陕西省水利工程质量检测中心站、西北水利水电工程建设监理中心、西北水利水电建筑勘察设计院、咸阳西北水利工程咨询有限公司、陕西省节水灌溉试验中心、测量测绘实验教学中心、电工电子实验教学中心等11个校级科技创新研究及社会服务平台，4个校内实践教学基地、19个校外实践教学基地，农业水工程实验教学示范中心2009年获批国家级实验教学示范中心；主办《水资源与水工程学报》、《水利与建筑工程学报》学术刊物，是陕西省水利学会、水力发电学会的理事单位。")]),v._v(" "),_("p",[v._v("学院现有教职工214人，其中教授及研究员25人，副教授及副研究员83人；博士生导师24人，硕士生导师52人。其中双聘院士1人，“后稷学者”特聘教授1人，国务院学位委员会学科评议组成员1人，国家“百千万人才工程”入选者1人，教育部高校青年教师奖获得者1人，教育部“新世纪优秀人才支持计划”入选者1人,“高等学校优秀青年教师资助计划”入选者2人，西北农林科技大学拔尖人才支持计划入选者3人。农田水利教学团队被评为省级教学团队。")]),v._v(" "),_("p",[v._v("学院现有学生2386人，其中本科生2073人，硕士研究生272人，博士研究生41人。77年来，先后为社会培养各类人才10200余名。著名水资源专家李佩成院士、力学家王光远院士、农业水土专家熊运章教授、长江学者特聘教授、农业水土专家康绍忠教授，现任陕西省副省长郑小明、陕西省政协副主席张生朝、原陕西省人大常委会副主任高宜新等是校友中的杰出代表。")]),v._v(" "),_("p",[v._v("在大学生创新能力培养方面，荣获全国“挑战杯”竞赛三等奖2项、全国大学生水利创新设计竞赛二等奖一项。2009年和2010年在中国智能机器人大赛中，荣获亚军1项、一等奖3项、二等奖4项、三等奖2项。")]),v._v(" "),_("p",[v._v("学院在农业水土、泥沙、旱区水资源、防渗抗冻胀、水工钢闸门、灌溉水力学、高速水流等学科领域，结合黄土高原环境改善及西北旱寒灌区建设的重大需求，研究成果显著。")]),v._v(" "),_("p",[v._v("上世纪70年代，熊运章教授等一大批农业水土专家创立的国家重点学科——农业水土工程学科面向旱区灌溉农业的需求，以学科交叉、渗透融合为突破口，强调水——土——作物的有机结合，工程措施和农业生物措施相统一，围绕我国西北及干旱半干旱地区的农业节水与水资源持续利用问题进行了全方位研究。在SPAC水分传输动力学及其节水机制、作物需水量计算与非充分灌溉理论、作物控制性分根交替灌溉、大田作物调亏灌溉、多泥沙渠道量水配水技术等领域的研究具有较高的水平。")]),v._v(" "),_("p",[v._v("黄土旱塬因水土流失导致泥沙问题普遍严重，上世纪50年代以来沙玉清等一大批泥沙专家，完成了三门峡水库泥沙治理、黄河及渭河流域产沙、河流泥沙、渠库泥沙等方面130余项科研任务，出版了《泥沙运动学引论》、《水库泥沙》、《引洪淤灌》等泥沙方面我国最早的经典著作。")]),v._v(" "),_("p",[v._v("西北地区水资源短缺、水土环境恶化，上世纪80年代在李佩成院士的带领下，积极开展人类活动下旱区水文规律、水土环境演变机理、区域水资源可持续利用等方面研究，已形成了明显的特色优势。")]),v._v(" "),_("p",[v._v("结合上世纪40年代我校水利学科创始人近代水利大师李仪祉先生兴建的关中八惠运行70年后老化冻害普遍，在旱寒区渠库及灌区建筑物老化防渗抗冻胀和水工闸门结构理论方面形成了鲜明的学科特色，编著《渠道防渗抗冻胀规范》等国家标准及专著多部，创办了《渠道防渗》杂志，举办十多期全国性渠库防渗抗冻胀研讨学习班，在全国形成了较大影响。")]),v._v(" "),_("p",[v._v("针对西部高水头水电工程消能，上世纪80年代在韩瀛观、李崇智、夏迈定、张志恒等教授带领下研究高速水流空蚀破坏机理及泄水建筑物体型，编写国家标准2部，研究成果被工程教材、专著及工程广为采用。")]),v._v(" "),_("p",[v._v("近年来学院先后承担国家重大科技攻关项目，国家重大基础性研究计划项目，国家“863”计划项目，国家节水农业重大专项、国家科技支撑计划项目、国家自然科学基金重大项目、国家杰出青年科学基金、国家农业高效用水重大产业化工程项目等国家、国际合作、省部级司局等课题130余项，各类工程科研项目400余项。获国家和省部级奖励20余项，其中，国家科技进步二等奖2项，省部级科技进步一等奖6项，省部级科技进步二等奖8项。在美国ASCE的《JournalofHydraulicEngineering》、《JournalofstructuralEngineering》、《JournalofIrrigation&DrainageEngineering》、《InternationalJournalofSedimentResearch》，中国《水利学报》、《泥沙研究》、《岩土工程学报》等重要学术刊物发表论文420余篇，被SCI、EI收录210余篇。")]),v._v(" "),_("p",[v._v("学院将坚持人才强院、学科主导，强化与旱区节水农业、水利土木工程重大建设项目的紧密结合，努力创造人尽其才，才尽其用的工作环境，全面提升学科水平，为建设以节水农业与水工程为特色的一流学科和高水平学院而努力奋斗。")]),v._v(" "),_("p",[v._v("学院本科就业率非常高，学生一般会选择本科毕业就工作，而且多半愿意留在西北地区工作，因为西北地区正在大力搞基础建设。不过如果十分优秀的话可以就进水土保持研究所。（中国科学院水利部水土保持研究所成立于1956年，是中国科学院在西北地区建立的第一个科研机构。全所现有职工220人，其中中国科学院和中国工程院院士各1人，国际欧亚科学院院士1名，正高级职称50人。拥有土壤学、生态学、水土保持与荒漠化防治3个博、硕士学位授权点，设立农业资源综合利用博士后流动站。现有在学研究生130余人。）")]),v._v(" "),_("p",[v._v("水利与建筑工程学院精品课程：")]),v._v(" "),_("p",[v._v("505")]),v._v(" "),_("p",[v._v("4.39西北农林科技大学")]),v._v(" "),_("p",[v._v("学院精品课程")]),v._v(" "),_("p",[v._v("序号课程名称课程负责人级别年度")]),v._v(" "),_("p",[v._v("1《灌溉排水工程学》汪志农教授国家2004年")]),v._v(" "),_("p",[v._v("1《灌溉排水工程学》汪志农教授省级2004年")]),v._v(" "),_("p",[v._v("1《灌溉排水工程学》汪志农教授校级2003年")]),v._v(" "),_("p",[v._v("2《地下水动力学》魏晓妹教授校级2003年")]),v._v(" "),_("p",[v._v("3《水力学》吕宏兴教授校级2004年")]),v._v(" "),_("p",[v._v("4《水文水利计算》刘俊民教授校级2004年")]),v._v(" "),_("p",[v._v("5《工程测量》李援农教授省级2011年")]),v._v(" "),_("p",[v._v("6《水泵及水泵站》把多铎教授校级2005年")]),v._v(" "),_("p",[v._v("7《水工建筑物》蔡江碧教授校级2005年")]),v._v(" "),_("p",[v._v("8《土力学及地基基础》党进谦教授校级2006年")]),v._v(" "),_("p",[v._v("9《土建工程制图与计算机绘图》裴金萍副教授校级2006年")]),v._v(" "),_("p",[v._v("10《工程地质与水文地质》刘俊民教授校级2007年")]),v._v(" "),_("p",[v._v("11《材料力学》连星耀副教授校级2007年")]),v._v(" "),_("p",[v._v("506")]),v._v(" "),_("p",[v._v("4.39西北农林科技大学")]),v._v(" "),_("p",[v._v("4.39.7.创新生科专业简介")]),v._v(" "),_("p",[v._v("本文于2013年录入/冉玲")]),v._v(" "),_("p",[v._v("专业名称：生物科学")]),v._v(" "),_("p",[v._v("学科分类：理学大类，生物科学类")]),v._v(" "),_("p",[v._v("修业年限：本科3年；本硕博8年")]),v._v(" "),_("p",[v._v("培养目标：本专业培养具备生物科学的基本理论、基本知识和较强的实验技能（包括大型的野外“秦岭”生物实验），能在科研机构、高等学校以及企业单位等从事科学研究、教学工作以及管理工作的生物科学高级管理人才。")]),v._v(" "),_("p",[v._v("培养学科：生物，生物化学，物理化学，农林气象学，数学，英语")]),v._v(" "),_("p",[v._v("毕业生应获知识：数学物理化学等方面的基本理论和基本知识；了解相近专业的一般原理和知识；了解国家科技政策、知识产权等有关政策和法规；了解生物科学的理论前沿、应用前景和最新发展动态。")]),v._v(" "),_("p",[v._v("专业选向：植物科学方向，动物科学方向；")]),v._v(" "),_("p",[v._v("特殊要求：大一选择方向，大二按照方向课表上不同的课。")]),v._v(" "),_("p",[v._v("主要课程：动物生物学，解剖学，动物微生物学，动物生物化学，生物化学，植物微生物学，细胞生物学，遗传学等")])])}),[],!1,null,null,null);_.default=t.exports}}]);