(window.webpackJsonp=window.webpackJsonp||[]).push([[216],{398:function(t,s,e){"use strict";e.r(s);var n=e(7),r=Object(n.a)({},(function(){var t=this,s=t._self._c;return s("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[s("h1",{attrs:{id:"林学院"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#林学院"}},[t._v("#")]),t._v(" 林学院")]),t._v(" "),s("blockquote",[s("p",[t._v("本文于2013年录入/佚名")])]),t._v(" "),s("p",[t._v("西北农林科技大学林学院（林业科学研究院）是培养高层次的林业人才和林业科学研究的重要基地。历经70多年的建设，已发展为涵盖农学、理学、工学、艺术学4个学科门类，学士、硕士和博士多层次、多学科协调发展的学院。")]),t._v(" "),s("p",[t._v("学院现有教职工203人，二级教授4人，三级教授8人。其中正高：33人(16%)，副高：65人(32%)，中级：75人(37%)，初级：22人(11%)；博士学位：56人(27.5%)，硕士学位：84人(41%)。有博士生导师21人，硕士生导师73人；双聘院士2人，“千人计划”特聘专家2人，国家杰出青年基金获得者1人，全国模范教师1人，教育部创新团队2个，国家及教学团队2个，国家精品课2门，陕西省优秀教师2人、教学名师2人。现有林学、林产化工、园林、艺术设计、森林资源保护与游憩等5个本科专业，17个本科教学实验室；具有林学博士后流动站，林学、生态学、风景园林学3个一级学科博士学位授予权和林业工程一级学科硕士学位授予权，林业、风景园林2个硕士专业学位授予权。在校本科生1602人，博士、硕士研究生678人。")]),t._v(" "),s("p",[t._v("学院拥有教育部西部环境与生态重点实验室、国家林业局黄土高原林木培育重点实验室、陕西秦岭森林生态系统国家野外科学观测研究站、国家林业局西北自然保护区研究中心、陕西省林业综合重点实验室、陕西省经济植物资源开发利用重点实验室、陕西省林业研究中心、国家“985”工程“西北森林生态系统保育与经营”科技创新平台等12个省部级和学校科技创新平台；拥有山阳核桃板栗试验示范站、清涧红枣试验示范站、凤县花椒试验示范站、镇安板栗试验示范站、安康北亚热带经济林果树试验示范站，以及渭河试验站、南五台试验站、杨凌教学试验苗圃3个教学基地。为创新型人才的培养和科学研究奠定了坚实的基础。学院承办的核心期刊《西北林学院学报》和主办的《陕西林业科技》学术期刊面向国内外公开发行。")]),t._v(" "),s("p",[t._v("新时期学院将继续弘扬传统、深化改革，全面实施人才强院战略，以学科建设为龙头，以创新型人才培养为重点，促进产学研紧密结合，立足西北，面向全国，为我国生态环境建设和实现林业可持续发展做出更大的贡献。")]),t._v(" "),s("p",[t._v("管理创新：以宿舍为单位，以宿舍为切入点对学生进行管理，最近还成立了宿管委员会。")])])}),[],!1,null,null,null);s.default=r.exports}}]);