(window.webpackJsonp=window.webpackJsonp||[]).push([[219],{401:function(t,s,e){"use strict";e.r(s);var n=e(7),r=Object(n.a)({},(function(){var t=this,s=t._self._c;return s("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[s("h1",{attrs:{id:"农学院"}},[s("a",{staticClass:"header-anchor",attrs:{href:"#农学院"}},[t._v("#")]),t._v(" 农学院")]),t._v(" "),s("blockquote",[s("p",[t._v("本文于2013年录入/佚名")])]),t._v(" "),s("p",[t._v("农学院是西北农林科技大学办学历史最悠久的学院之一。学院现设农学、植物科学与技术、种子科学与工程3个本科专业。作物栽培学与耕作学、作物遗传育种、植物资源学、种子工程、农业推广等七个硕士学位授权点；作物栽培学与耕作学、作物遗传育种、植物资源学、种子工程等4个博士学位授权点，并设有作物学博士后流动站。")]),t._v(" "),s("p",[t._v("学院现有教职工231人，其中教授、研究员36人，副教授、副研究员77人；博士生导师21人，硕士生导师61人；教师中国家及省部级突出贡献专家17人，教育部“新世纪优秀人才支持计划”入选者3人，“三秦学者”1人，陕西省“三五人才”10人，陕西省教学名师1人。")]),t._v(" "),s("p",[t._v("学院现有学生1283人，本科生928人、硕士研究生267人、博士研究生88人。1936年开始招收本科生，75年来，先后为社会培养各类人才13491人。著名小麦育种学家，中国科学院院士赵洪璋教授，中国农业区划的开拓者沈煜清教授，原农业部副部长路明同志，现任陕西省副省长朱静芝同志等是校友中的杰出代表。")]),t._v(" "),s("p",[t._v("身为农学院的同学们有百分之七十都打算继续深造，为国家的小麦育种方面作出突出贡献；只有百分之三十的同学计划本科毕业找工作。这一数字已经诠释了学院的奋斗目标：为建设以我国旱区农业为特色的世界一流农学学科而努力奋斗。农学院的孩子会永远朝着这个目标奋力向前的。")])])}),[],!1,null,null,null);s.default=r.exports}}]);