(window.webpackJsonp=window.webpackJsonp||[]).push([[229],{410:function(v,_,t){"use strict";t.r(_);var a=t(7),s=Object(a.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("h1",{attrs:{id:"生命科学学院"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#生命科学学院"}},[v._v("#")]),v._v(" 生命科学学院")]),v._v(" "),_("blockquote",[_("p",[v._v("本文于2013年录入/高2012级27班 刘春宏")])]),v._v(" "),_("h2",{attrs:{id:"学术氛围"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#学术氛围"}},[v._v("#")]),v._v(" 学术氛围")]),v._v(" "),_("p",[v._v("作为北大的理科院系，学术氛围挺不错的。在这里，你可以见识到国际奥赛金牌的大神，还有大一下就进入实验室的大牛。如果你想专心刷题，图书馆教学楼的条件很不错。身边不乏努力学习的同学，院里的学术讲座也不少（英文的，略显高端，笔者表示一次没去过……）。总的来说，硬件软件条件都很不错。")]),v._v(" "),_("h2",{attrs:{id:"活动氛围"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#活动氛围"}},[v._v("#")]),v._v(" 活动氛围")]),v._v(" "),_("p",[v._v("院系里的活动不少，征文，摄影，唱歌，体育，各种各样都有。除了院里，学校里还有其他院的活动以及一些全校性的活动，如果你想参加，绝对不愁没有活动。")]),v._v(" "),_("h2",{attrs:{id:"学院专业"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#学院专业"}},[v._v("#")]),v._v(" 学院专业")]),v._v(" "),_("p",[v._v("生命科学 生物技术")]),v._v(" "),_("h3",{attrs:{id:"专业介绍"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#专业介绍"}},[v._v("#")]),v._v(" 专业介绍")]),v._v(" "),_("p",[v._v("我们院大三分专业，在课程上，两个专业的区别在于生物技术多了两门课。在专业性质上，两个专业都是理学学士学位，区别不大。填志愿的时候就更不用纠结了。")]),v._v(" "),_("h3",{attrs:{id:"培养目标"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#培养目标"}},[v._v("#")]),v._v(" 培养目标")]),v._v(" "),_("p",[v._v("注重综合素质的培养，兼顾前沿和基础知识的传授，强调实践和创新能力，以培养生物科学研究，生物技术开发、生物知识产权管理、生物企业管理等方面的高层次优秀人才。（以上来源于北大生科院官方网页）实际教学中还是以科学知识及科学思想方面的居多，重点还是培养科学研究人员。")]),v._v(" "),_("h3",{attrs:{id:"就业方向"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#就业方向"}},[v._v("#")]),v._v(" 就业方向")]),v._v(" "),_("p",[v._v("目前生命科学学院的本科生中有80~85%继续攻读国内外著名大学和科研院所的研究生，在国内就业的本科生可通过与用人单位的双向选择，到科研、国家管理部门、教学、医药、生物工程公司等单位工作。（以上来源于本科生学习指导手册）很大一部分人本科后都会申请出国。因为出国人数较多，所以保研名额相对比较充足。但是因为大家都喜欢留本校保研，留校名额比较抢手，成绩太差垫底的话，还是有些危险的。本科后直接就业的人很少。 在北大生科院里大概有三类人，一类是竞赛报送的大牛们，一类是报考数学物理信科不成，报考生科准备转系的。还有一类就是对生物有一点兴趣，或者想学理科但是觉得数理化太难而报考生科的。诚然，生科比物院化院轻松一些，但毕竟也是理科院系，不要幻想每天早上睡懒觉的生活。如果想要认真学的话，虽然达不到高三的忙乱，但至少也是高一高二的程度。虽然学的内容没有数学物理那么深，但是我们学得广，需要上的课更多，并且有时候还要求阅读英语论文。所以基本上大一都挺忙的，据学长说到了大二下就好些了，这时一般就可以进入实验室了。 生科院最近转系的人日益增多，因为有人觉得生科就业前景不好而犹豫。前几年生物热门了一阵，现在国内的产业没有发展起来，又冷清下来了。2012年北大生科院的招生人数就减少到了100人左右，今年应该也差不多。不过院里的老师对生物的前景还是挺乐观的，现在应该是达到了一个低谷，随着越来越多物理化学的开始做生物，以后一定会有反弹，只是，存在时间问题。作为全国数一数二的生物院系，如果你真的对生物有浓厚的兴趣，想要进行科研工作，这里会给你提供良好")]),v._v(" "),_("p",[v._v("的发展条件的。这里必须说一下，科研是一条很长的路，要做出成绩通常要10年，所以一定要有心理准备。 另外还有北大与清华的生科院的比较。北大可以说是历史悠久，植物学是传统强项。清华建系较晚，最近引进了很多留学归来的学者，主要是蛋白质结构方面的成果比较突出。至于本科生教学质量，由于没有亲身比较，不好评价。不过笔者对北大的专业课教学还是挺满意的。其实北大和清华的生科院有很多合作，比如我们可以选一些清华的本科生课程，以后进实验室也可以选择去清华。如果你真的想报考生物，可以和两边的老师都交流一下，和两个学校里的南开前辈们联系一下再做决定。")]),v._v(" "),_("h3",{attrs:{id:"专业课详细介绍"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#专业课详细介绍"}},[v._v("#")]),v._v(" 专业课详细介绍")]),v._v(" "),_("p",[v._v("北大生科院在饶毅任院长期间进行了一些改革，其中一项就是选课更加灵活，更加自由。现在我们的必修课除了公共必修课（英语，体育，政治，计算机）和理科基础课（高数，物理，化学等），专业必修课减少到五门，即生物化学，遗传学，分子生物学，细胞生物学，生物学思想与概念。而其他的课程，如植物学，动物学，以及各类实验等，都可以依据你的爱好选择，学分紧张时甚至可以采取只听不选的策略，并且如果你对本院开的课不满意，可以跨院选课。 接下来说说已经上过的专业课。其实进校之后会拿到一本《生命科学学院本科生学习指导手册》，里面对开的各门课都有详细介绍。这里就简单写一写个人感想，作为参考。 专业课整体情况是内容偏多，时间偏紧，有些东西老师可能会讲得很简单，下去之后就要自己看书归纳。大一接触的专业课内容多半靠记忆，与高中知识重叠不多，高中学的那些基因的转录与翻译什么的大概要大二的分子生物学和生物化学才会专门讲解。 动物生物学及实验 基本上是动物的演化史及动物的解剖学，可以说，内容以记忆为主，理解是在记忆的基础上的，没有兴趣的同学会显得很枯燥。实验课安排是4个小时，前半学期基本是看装片，将显微镜使用得得心应手。后半学期会进入解剖阶段，蚯蚓，蝗虫，鲤鱼，小白鼠都会上手，还会有蟾蜍和小白鼠的处死，并且，尖叫会扣分……")]),v._v(" "),_("h3",{attrs:{id:"教材"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#教材"}},[v._v("#")]),v._v(" 教材")]),v._v(" "),_("p",[v._v("《动物生物学》 许崇任 程红编，高等教育出版社")]),v._v(" "),_("p",[v._v("生物学思想与概念")]),v._v(" "),_("p",[v._v("这是饶毅当院长之后新开的一门课，并且有几节课他会亲自来讲。课堂主要是讲一些研究的案例，各个领域的发展进程，研究思想等。虽然教务将其排在大一的课表里，但就我个人不推荐大一就上，反正对我而言这门课的意义就在于刷全英论文了……另外，不要对这门课期待太高，思想这些东西，都是个人体悟总结出来的，不要期待上完这门课就全懂了。")]),v._v(" "),_("p",[v._v("植物生物学及实验")]),v._v(" "),_("p",[v._v("主要内容是植物生理和演化，大部分内容与高中无关。讲课质量超赞，目前上过最好的专业课，并且老师会特别注重科学思维的培养和考察，不过记忆还是很重要。实验课基本上是看装片，一看就是一下午，明显没有动物学实验有趣。每学期有一次校园植物辨识，可以期待一下。")]),v._v(" "),_("p",[v._v("教材：《植物生物学》 杨继主编，高等教育出版社")]),v._v(" "),_("p",[v._v("微生物学")]),v._v(" "),_("p",[v._v("主要内容为微生物的结构及代谢。本来是大三的专业课，课程改革之后调到大一来上，老师讲得一般，内容枯燥，记忆较多，口碑不太好，很多同学都没有选。其实专业课质量参差不齐，主要还是要靠自己。")]),v._v(" "),_("p",[v._v("教材：《微生物学》 沈萍 陈向东主编，高等教育出版社")]),v._v(" "),_("p",[v._v("理科基础课")]),v._v(" "),_("p",[v._v("大学数理化课程每周只上两次课，并且物理半学期就要讲完一本书的内容，所以不要幻想到大学里可以慢慢学，如果没有自己的课外练习，对知识的掌握程度很难达到高中的水平。并且大学数学是建立在微积分的基础上的，大学物理是建立在大学数学的基础上的，大学化学很多要用到大学物理的知识，所以物理化学和高中的是有相当大的区别，如果没有接触过一些竞赛知识的话，到了大学里你很容易就会发现之前的兴趣被现实打败了。")]),v._v(" "),_("p",[v._v("教材：都是北大出版社的教材")]),v._v(" "),_("p",[v._v("公共必修课")]),v._v(" "),_("p",[v._v("英语是入校之后会有分级考试，分到几级就从几级开始上。要求四年修满四门英语课，一定要修完大英四级。比如分级考试分到三级就要上大英三级、大英四级，再自己选两门专题课。所以分级考试考差了也不一定是坏事。体育课也是大学里必须修满四门，男生必修太极拳，女生必修健美操。开的课程很多，有些很火的课可能四年都选不上……")]),v._v(" "),_("p",[v._v("通选课")]),v._v(" "),_("p",[v._v("北大的通识教育课程，全校的同学都可以选，课程也很多，涵盖很广。不过作为生科院的孩子，一定要有大二大三再选的觉悟……")]),v._v(" "),_("h3",{attrs:{id:"分数线参考"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#分数线参考"}},[v._v("#")]),v._v(" 分数线参考")]),v._v(" "),_("p",[v._v("笔者当年高考分数是685分，全市排名前二十。其实生命科学专业每年都没有多")]),v._v(" "),_("p",[v._v("少人报，2012年重庆就收了两个人，所以只要上了线，你有这方面的意愿，应该就能报到。（其实这个位置的成绩可以慢慢跟他谈，不要急于下结论，到最后时刻说不定可以拿到经济或其他热门专业。）")]),v._v(" "),_("p",[v._v("以上仅为笔者个人意见，难免存在不客观的认识和说法，请大家选择性接受。")]),v._v(" "),_("p",[v._v("总而言之，北大生科欢迎各位对生物有兴趣，有理想的同学们前来。现在已经连续三年每年都有一个南开的报考了，所以新同学来了之后就可以收到学长学姐们代代相传的教材了~")])])}),[],!1,null,null,null);_.default=s.exports}}]);