(window.webpackJsonp=window.webpackJsonp||[]).push([[231],{412:function(_,v,t){"use strict";t.r(v);var a=t(7),s=Object(a.a)({},(function(){var _=this,v=_._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":_.$parent.slotKey}},[v("h1",{attrs:{id:"北大医学部药学院"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#北大医学部药学院"}},[_._v("#")]),_._v(" 北大医学部药学院")]),_._v(" "),v("blockquote",[v("p",[_._v("本文于2015年录入 / 高2013级3班 莫玉霖")])]),_._v(" "),v("h2",{attrs:{id:"学术氛围"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#学术氛围"}},[_._v("#")]),_._v(" 学术氛围")]),_._v(" "),v("p",[_._v("北医药学专业历来全国排名第1，所以就专业本身而言确实是无可挑剔。师资介绍参看药学院官网：http://sps.bjmu.edu.cn/xyjs/index.htm 很厉害就是了。")]),_._v(" "),v("p",[_._v("值得一提的是药学院拥有一个国家重点实验室（北医仅此1个，北大只有6个），整体的硬件设施和科研资源都非常好。搞研究发论文的好地方！")]),_._v(" "),v("p",[_._v("学院每年会有“大学生创新计划”，可以在大二大三的时候就进实验室，开始做一些小课题。此外大二的时候有“实验室轮转”，可以在大二就接触科研，整体科研氛围还是很浓的。")]),_._v(" "),v("h2",{attrs:{id:"活动氛围"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#活动氛围"}},[_._v("#")]),_._v(" 活动氛围")]),_._v(" "),v("p",[_._v("药学院学生会办的活动是北医各院学生会中最多的，有元旦晚会、舞会、新生篮球赛、实验技能大赛等等。但和北大那边相比还是逊色不少，来了这里专心学术吧- -")]),_._v(" "),v("h2",{attrs:{id:"学院专业"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#学院专业"}},[_._v("#")]),_._v(" 学院专业")]),_._v(" "),v("p",[_._v("本科阶段：药学专业，不分方向。研究生阶段：化学生物学系，药物化学系，天然药物学系，药剂学系，分子与细胞药理学系，药事管理与临床药学系。")]),_._v(" "),v("h2",{attrs:{id:"本科培养计划"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#本科培养计划"}},[_._v("#")]),_._v(" 本科培养计划")]),_._v(" "),v("p",[_._v("药学院本科只有药学一个专业，不分方向，大家学的都是一样的。从培养计划上可以发现课程主要分三类：")]),_._v(" "),v("p",[_._v("化学类：无机化学、有机化学、分析化学、仪器分析、波谱解析等等")]),_._v(" "),v("p",[_._v("生物类：系统解剖学、人体生理学、药用植物学等")]),_._v(" "),v("p",[_._v("医学类：药理学、免疫学、微生物等等（基本就是前两类复合一下）")]),_._v(" "),v("p",[_._v("其中重中之重是化学，所以化学好的孩子，学过化竞的孩子非常有优势！从另一方面来说，如果你对化学和生物兴趣不足，那么就要慎重考虑是否要读这个专业了。因为越到后面越枯燥，学的内容也越难，如果前面有机化学学得不好，那么后面药物化学就比较吃力。此外，有大量实验课，大三基本就是每天下午都在做实验，所以对实验无感的孩子慎入- -")]),_._v(" "),v("p",[_._v("学院药学研究采取的是西医的研究方法，所以学习重心在化学上，课程中虽然有涉及中医的内容，但是仍然采取的西医的方法研究，比如检测药材中究竟有哪些生物活性分子，提取这些分子做药，并非传统的中医理论学习。")]),_._v(" "),v("p",[_._v("当然，也不用太担心自己基础的问题，关键是有兴趣。学长我就属于高考党，不巧的是，以前学的最糟糕最讨厌的就是化学，阴差阳错学了药学，也没啥兴趣，但是只要按部就班地学习，跟着老师走，学业也不算太坏。南开出来的孩子，都是没问题的！")]),_._v(" "),v("h2",{attrs:{id:"就业方向及就业率"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#就业方向及就业率"}},[_._v("#")]),_._v(" 就业方向及就业率")]),_._v(" "),v("p",[_._v("这里要谈一下药学院的学制了。药学是6年制，在大四上学期的时候分研究生")]),_._v(" "),v("p",[_._v("方向，如果这个时候不想读研，也可以选择4年出口，直接工作或者出国。药学院一届学生有120人，一般选择4年出口的在20人以下。谈谈各个研究生方向都是干什么的吧。")]),_._v(" "),v("p",[_._v("药化：主要是搞有机合成，和药剂一起是每年分流的热门。也是就业最好的一个方向，药企、科研单位都需要。药化可以算是整个制药工业中的核心了，毕竟药物起效就是靠的他们合成出来的这些玩意儿。如何合成具有靶向、具有生物活性的药物分子，这就是他们的研究内容。缺点就是工作任务繁重而且毒性太大，整天在实验室里搞有机合成，那些有机物一个比一个毒，吸多了还是不太好……")]),_._v(" "),v("p",[_._v("药剂：药剂主要研究的是如何通过改善药物剂型（丸剂、片剂、纳米……）来提高药物作用效果，算是药化接下来的一环，也是相当重要。就业形势和药化差不多，可能工资稍低一点，但药剂的工作环境比药化好很多，因为有机合成做得少，主要是生物活性检测。对有机化学的功底要求也不是那么高，很不错的一个方向。")]),_._v(" "),v("p",[_._v("药理：偏医学，动物实验做得比较多，主要探究药物的生理作用机理，和基础医学的研究有些相似。药学院各方向其实都比较偏化学，医学涉及的比较少，药理算是比较例外的，也不用做那么多化学实验。")]),_._v(" "),v("p",[_._v("药分：主要从事药物分析，主要运用分析化学的手段进行研究，相比药化药剂要轻松一些，以后主要是搞检测。")]),_._v(" "),v("p",[_._v("天药：研究天然药物（很多都是中药药材），提取药材中的生物活性物质，研究它们的结构和生理活性。这个方向也属于平时相对轻松，但论文相对难发的一个方向。天然药物前景很广，但不确定性也太大。像青蒿素这样的药物，估计好几十年才能出一个。")]),_._v(" "),v("p",[_._v("临药&药管：这个方向最大的特点是不用做实验……临药研究生阶段就会进医院轮转，从药房抓药到病室探镇，医院的各部门都会走一圈，最后的结果是当临床药师。临床药师在国外权力很大，也很重要，但国内尚未发展起来。现实普通，前景光明。药管的话，毕业主要去药监局和行政机构，偏重管理。")]),_._v(" "),v("p",[_._v("还想了解更多可以参见："),v("a",{attrs:{href:"http://sps.bjmu.edu.cn/xkjs/",target:"_blank",rel:"noopener noreferrer"}},[_._v("http://sps.bjmu.edu.cn/xkjs/"),v("OutboundLink")],1)]),_._v(" "),v("h2",{attrs:{id:"考研-出国方向"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#考研-出国方向"}},[_._v("#")]),_._v(" 考研/出国方向")]),_._v(" "),v("p",[_._v("因为是6年制，所以基本不存在考研的问题。出国的话，就比较尴尬了。")]),_._v(" "),v("p",[_._v("如果选择4年出口直接出国，那很难申请到特别好的学校，因为本科阶段还在学习基础理论呢，实验室还没泡够，没发论文，国外的大学看不上。当然，也有个别大神从大二开始就泡实验室，不仅绩点高，最后还发了论文，成功去加州大学伯克利分校的。")]),_._v(" "),v("p",[_._v("如果6年读完再出国呢？这同样也比较坑。因为我们毕业时拿的是理学硕士，如果去国外只能申请Ph.D.（研究性博士）如化学类的，而不能申请M.D.（医学博士），因为在国外药学是划入医学专业的。但我们的本科学位国外是认可的，也就是说你要出国，那就还需要重新从研究生读起。（2年的光阴啊！！）")]),_._v(" "),v("h2",{attrs:{id:"实验室"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#实验室"}},[_._v("#")]),_._v(" 实验室")]),_._v(" "),v("p",[_._v("这个有必要单独拿出来说一下。")]),_._v(" "),v("p",[_._v("个人觉得，对于一个热爱化学，喜欢药学的人来说，学校提供的资源真是足够好了，想多学术就可以多学术（可惜我不属于这类人，sigh）体现在实验室上就是学院有自己的实验室轮转制度和大学生创新实验项目。")]),_._v(" "),v("p",[_._v("这里主要说一下实验室轮转。轮转是从大二开始的。上面提到药学研究生会分好几个方向，在读本科的时候肯定都不了解，于是实验室轮转制度就应运而生了。在大二这一年，我们会把主要几个方向的实验室都转一遍（每个方向半学期）在实验室轮转的时候，我们会归入老师的课题组，由研究生师兄师姐带着做实验，了解他们的研究内容，也会跟着他们听组会。也就是说我们在大二就可以接触到研究生阶段做的事，如果你足够厉害，这个时候就可以“进组”，选课题，做实验了。我身边甚至有同学暑假都不回家留在学校做实验的……所以说，只要你愿意，科研离你并不远。")]),_._v(" "),v("h2",{attrs:{id:"最后"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#最后"}},[_._v("#")]),_._v(" 最后")]),_._v(" "),v("p",[_._v("说一下我自己的感受吧，北医的药学毫无疑问是很优秀的，但我自己并不是特别喜欢，志不在此呀！所以说，学弟学妹们一定要好好考虑，不要一时脑热做了苦逼的医学生……欢迎有志青年报考北大医学部药学专业！啦啦啦~大家高考完了就好好浪吧！")])])}),[],!1,null,null,null);v.default=s.exports}}]);