(window.webpackJsonp=window.webpackJsonp||[]).push([[232],{415:function(v,_,t){"use strict";t.r(_);var a=t(7),r=Object(a.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("h1",{attrs:{id:"元培学院"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#元培学院"}},[v._v("#")]),v._v(" 元培学院")]),v._v(" "),_("blockquote",[_("p",[v._v("本文于2015年录入/高2014级3班 刘人榕")])]),v._v(" "),_("h2",{attrs:{id:"雪花的快乐-我有我的方向"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#雪花的快乐-我有我的方向"}},[v._v("#")]),v._v(" 雪花的快乐——我有我的方向")]),v._v(" "),_("p",[v._v("题目是元培学院2014级一二九大合唱的时候唱的歌。")]),v._v(" "),_("h2",{attrs:{id:"自由选择课程和专业方向"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#自由选择课程和专业方向"}},[v._v("#")]),v._v(" 自由选择课程和专业方向")]),v._v(" "),_("p",[v._v("元培学院的学生可以自由选择课程和专业方向。")]),v._v(" "),_("p",[v._v("首先要说明一下自由选择课程的意思。在北大，每个院在专业课上只能选择自己院系开设的课程，比如物理学院的同学不能选化院的高等数学课。如果想跨院系选课需要向教务递条子，并且在选课人数很多的情况下有可能选不上课。但是元培学生可以选所有院系的专业，并且选择的时候都是推荐，意思是对方院系把元培学生当做本院的学生对待。")]),v._v(" "),_("p",[v._v("对于专业方向，全校所有专业方向都是对元培开放的。所以我认为元培学院对我来说像一个小北大（或者说它是未来的北大），所有不同专业方向的同学们在这里自由的交流、争论。这种选择是多元化的，选择光华和经济的同学并不多，相反，大神们会选择自己真正感兴趣的专业。并且专业分布是平均的，即使北大最冷门的专业也有一两个人选择。不是调剂，这是真正的出于自己的意愿。")]),v._v(" "),_("p",[v._v("很多人在大一的时候选了多个专业方向的课，都去学了学，最后找到了最适合自己的方向，这也是元培的初衷之一吧。")]),v._v(" "),_("p",[v._v("也有很多人，在大一对某一个专业方向的学习中，感到不满，于是转到另外的方向，跨度也非常的大，从光华到物理，从化学到中文，从整合科学到计算机……有人说，没换过专业，怎么好意思说自己是元培的？嗷。我没有换过。")]),v._v(" "),_("p",[v._v("对于方向的选择，学生具有自主权。没人管你选什么专业，只要你想你就能选，这里有例外是光华选专业需要面试，但是对于你选择光华方向并没有太大的影响。")]),v._v(" "),_("h2",{attrs:{id:"弹性学习年限"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#弹性学习年限"}},[v._v("#")]),v._v(" 弹性学习年限")]),v._v(" "),_("p",[v._v("三年到六年的弹性本科学习年限，也是为元培量身设计。不过我认为大部分人不需要care这件事。因为三年毕业真的很难也不值得！同时，元培学院关于每个专业的毕业学分要求都比该院系该专业的学生低20-30分。所以即使换方向四年也是可以轻松毕业的。")]),v._v(" "),_("h2",{attrs:{id:"特色课程和跨学科专业"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#特色课程和跨学科专业"}},[v._v("#")]),v._v(" 特色课程和跨学科专业")]),v._v(" "),_("p",[v._v("元培学生的英语课和政治课是专为元培学生开设的（其他院系都统一上全校大课）。关于英语课：中教都是北大最好的英语老师，我们用的书都是她们编的……外教主要教写作，作业比较多……当然，虐，不过能真正学到东西，老师都非常非常好。政治课与一般政治课不同，采用大班教学+小班讨论的方式，虽然大班教学还是比较无聊，但是小班讨论撕逼撕得非常欢脱，在这种时候就能清晰的体会到来自不同专业的同学思想交流的过程。")]),v._v(" "),_("p",[v._v("另外，我们的军理课也是单独开设的，为网络授课+见面课。见面课有时也是跨校直播，网络课弹幕那叫一个醉……")]),v._v(" "),_("p",[v._v("跨学科专业指PPE（政治、经济与哲学）、整合科学（数学+理综）、数据科学（数学+计算机，目前没开，院长说15年会开）、古生物学、外外（外国语言与外国历史）。个人感觉学起来都很虐，但是真大神大部分都在这些方向里。")]),v._v(" "),_("h2",{attrs:{id:"集中住宿·男女混住-学术委员会"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#集中住宿·男女混住-学术委员会"}},[v._v("#")]),v._v(" 集中住宿·男女混住&学术委员会")]),v._v(" "),_("p",[v._v("所有元培学生都住在36楼，男生住一二三楼，女生住四五六。这样的好处是显而易见的，院内学生交流更频繁了，方便一起上自习、讨论。我们七个人（男女都有）约每周日在一楼讨论室一起做高数，杀一双拖鞋就下去了，特别方便。")]),v._v(" "),_("p",[v._v("男生不可以进女生寝室（有门禁），女生可以进男生寝室（慎重！小心看见什么不好的东西）。")]),v._v(" "),_("p",[v._v("36楼有楼委会，每天楼下的大屏幕会对当天过生的同学进行生日祝福，大家路过都可以看到。并且楼委会负责楼内设施和福利管理。（PS楼委会福利很多推荐加入）。")]),v._v(" "),_("p",[v._v("元培有一个特色学生组织简称学学学，全称大概是元培学院学生学术委员会，我也不太清楚名字，太长了记不住！主要负责各种学术活动。所以不要担心在元培没有学术活动的施展空间。并且以兴趣小组形式聚集对某方向学术有意向的同学。")]),v._v(" "),_("h2",{attrs:{id:"北-清"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#北-清"}},[v._v("#")]),v._v(" 北&清")]),v._v(" "),_("p",[v._v("当时我也在北大和清华之间纠结了很久。但是至少现在我是绝对不后悔的。")]),v._v(" "),_("p",[v._v("不谦虚的说，北大是自由浪漫的，而清华是严谨务实的（清华经管不是，表打我）。清华讲行胜于言，而北大是思想自由。我觉得也不是一定要选择跟自己气场相符的，只是给个参考吧。")]),v._v(" "),_("p",[v._v("至于寝室、环境，没错清华的寝室比北大好，我简直羡慕得要死。可是我的大学不是享受啊。")]),v._v(" "),_("p",[v._v("另外说一句，元培36楼目前已经是北大最好的宿舍了，而元培新楼正在盖，16年就可以用了。")]),v._v(" "),_("p",[v._v("刘人榕")]),v._v(" "),_("p",[v._v("邮箱liurenrong@pku.edu.cn")])])}),[],!1,null,null,null);_.default=r.exports}}]);