(window.webpackJsonp=window.webpackJsonp||[]).push([[260],{442:function(v,_,t){"use strict";t.r(_);var s=t(7),p=Object(s.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("h1",{attrs:{id:"财政金融学院-税收专业"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#财政金融学院-税收专业"}},[v._v("#")]),v._v(" 财政金融学院——税收专业")]),v._v(" "),_("blockquote",[_("p",[v._v("本文于2017年录入/高2013级 邓嫣语")])]),v._v(" "),_("p",[_("strong",[v._v("专业简介：")])]),v._v(" "),_("p",[v._v("税收专业主要培养理解国家税收政策和税收制度以及分析企业税务发展战略的能力，使学生成为能胜任税收领域实际工作的复合型人才。税务专业的对口方向是税务系统公务员、会计师事务所税务筹划方向、企业财务、银行等等。")]),v._v(" "),_("p",[_("strong",[v._v("学院简介：")])]),v._v(" "),_("p",[v._v("人大财政金融学院的课程设置有个特点，即全院都需要学习每个专业的学科基础课，如金融、国际金融、投资学、保险学、财政学、税收学等，培养复合型人才。因为有丰富的选修资源，所以对自己专业不满意的完全可以多修其他专业的课，毕业拿第二学位或者第二专业。财政系下有两个专业，财政学与税收学，分别设立财政班和税收班，两个专业学的课程基本一致，只有到了大三才会有专业选修课的区别。同时，在大一刚入学时，学院会通过英语分级考试、数学考试和面试，选择20名财政系的同学进入财税实验班学习。")]),v._v(" "),_("p",[v._v("此“实验班”和“普通班”与高中时的概念不同，学院设立实验班，是希望培养理论型人才，实验班和普通班相同的课程中，实验班学习的部分更加强调理论性和逻辑性，而普通班更加偏向实务方面；在不同的课程中，实验班比起普通班有更多的双语教学、更多的数学课程、更难的数学课程，所以数学不好的同学需要慎重考虑是否进入实验班。（我们这一届实验班数学挂科的基本都是文科生，班级排名靠前的基本都是理科生，实验班的数学学A难度，注重证明和分析，普通班的数学学C的难度，注重计算和解答。）据实验班的小伙伴说，在修了所有要求的数学必修的前提下，他们只要再修4门数学，就可以拿到数学第二学位（但是那4门很难），喜欢挑战的同学可以试一下，毕竟实验班的很多资源还是不错的。")]),v._v(" "),_("p",[_("strong",[v._v("课程设置：")])]),v._v(" "),_("p",[v._v("​    由于笔者已经大四了，不太清楚现在新一届大一的培养计划，只写几个当时我们上过的与专业相关的课程。")]),v._v(" "),_("p",[v._v("全校共同课：英语类、数学类（A/C）、计算机类、思想政治类（主要在大一和大二修完）")]),v._v(" "),_("p",[v._v("大一：微观经济学、宏观经济学、会计学、政治经济学")]),v._v(" "),_("p",[v._v("大二：统计学、保险学、财政学、税收学、公司财务（大一和大二由于有数学、英语，专业课设置比较少，建议在大一大二时期修满选修课的学分，给专业课较多的大三留出学习的时间）")]),v._v(" "),_("p",[v._v("大三：金融学、国际金融、中国税制、国际税收、比较税制、税务会计、纳税筹划、财政管理、计量财政学、投资学、商业银行业务与经营、计量经济学、公共政策分析")]),v._v(" "),_("p",[v._v("大四：税务代理（财金学院在大三下期和大四设置的课程都很少，方便同学实习、准备读研和找工作）")])])}),[],!1,null,null,null);_.default=p.exports}}]);