(window.webpackJsonp=window.webpackJsonp||[]).push([[291],{473:function(v,_,p){"use strict";p.r(_);var t=p(7),a=Object(t.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("h1",{attrs:{id:"投资学-1"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#投资学-1"}},[v._v("#")]),v._v(" 投资学（1）")]),v._v(" "),_("blockquote",[_("p",[v._v("上海财经大学公共经济与管理学院")])]),v._v(" "),_("p",[v._v("投资学")]),v._v(" "),_("p",[v._v("本文于2017年录入/2016级19班陈鸣")]),v._v(" "),_("p",[v._v("我先介绍一下投资学这个专业。")]),v._v(" "),_("p",[v._v("对于培养出什么样的人才，官方的说法是培养具有扎实的投资管理理论基础、具备投资项目分析与管理的能力、能够独立从事政府、跨国企业和金融机构等多个领域的投资经济及管理工作的高级工作专门人才。")]),v._v(" "),_("p",[_("strong",[v._v("一、培养计划")])]),v._v(" "),_("p",[v._v("我们学校将课程分为通识教育课程、学科共同课、专业课和个性化培养课程。")]),v._v(" "),_("p",[_("img",{attrs:{src:"//pic.kuaizhan.com/g1/M00/C1/17/wKjmqVkv-HuAOcPaAAAAKwBVcbI7511162",alt:"ZW@[PAGPTJB3F)L}]%BCEHY"}})]),v._v(" "),_("p",[_("img",{attrs:{src:"//pic.kuaizhan.com/g1/M01/9D/DE/CgpQU1kv-HuAGyG6AAAAKwBVcbI1667333",alt:"8[)O9N9}P040F5]5L4WNAO8"}})]),v._v(" "),_("p",[_("img",{attrs:{src:"//pic.kuaizhan.com/g1/M01/9D/DE/CgpQU1kv-HuAGyG6AAAAKwBVcbI1667333",alt:"G`6Q}@Y[VE2)(]18CLZ$}}O"}})]),v._v(" "),_("p",[v._v("![RUPI_O@J]2N1TU_)3A6B8B7]"),_("a",{attrs:{href:"//pic.kuaizhan.com/g1/M01/9D/DE/CgpQU1kv-HuAET1EAAAAKwBVcbI7541925"}},[v._v("2")])]),v._v(" "),_("p",[v._v("通识教育课程就是基本上所有大学生都需要修习的课程，比如高数、体育、毛概……我们学校还将通识教育课程中的选修课程分为限选课和选修课。限选课相比选修课当然就是有更多限制，这里就不再多说。")]),v._v(" "),_("p",[v._v("学科共同课包括必修课财务会计、中级微观经济学、中级宏观经济学、财务管理、货币银行学、财政学；选修课管理学、运筹学、管理会计、市场营销学、经济法概论、国际金融、国际贸易。")]),v._v(" "),_("p",[v._v("投资学的专业课包括必修课投资经济学、投资项目管理、建筑工程概论、证券投资学、风险投资学、融资学、工程造价估算、投资项目评估、国际投资学；选修课公共投资、期货投资、投资银行学、并购与重组信托投资学。")]),v._v(" "),_("p",[v._v("个性化培养课程将选择方向（即直接就业、继续深造、自主创业）不同的学生课程分成了三种，这里也不再一一列举。")]),v._v(" "),_("p",[v._v("二、专业特色")]),v._v(" "),_("p",[v._v("要说专业特色的话，那就是我们的投资是涉及到基建和证券等方面的比例基本是相等的。金融专业主要是证券方面，房地产专业的同学则更多是基建方面。如果以后想要从事研究型的工作，可能经济学这样的专业更适合你，如果是想从事更实际的工作，又不知道更喜欢哪一方面，那投资学真的是一个非常不错的选择（毕竟还不用学数学分析）。反正学了一年左右的时间后，还能转专业或者学第二专业:-D当然这两个都是要看绩点的。听辅导员说，投资学这个专业还没有人转过专业。")]),v._v(" "),_("p",[v._v("三、第二专业")]),v._v(" "),_("p",[v._v("财大最热门的，不管是第一专业还是第二专业，那一定都是会计学和金融学。我个人是选的金融学作为第二专业。")]),v._v(" "),_("p",[v._v("金融学第二专业的课程分为专业核心课、专业方向课和专业选修课。专业核心课有保险学原理、投资学、公司金融、商业银行经营管理。专业方向课有财务报表分析、金融工程学、中央银行与监管、国际信贷、固定收益。专业选修课有金融计量学、国际金融、货币银行学、资产组合管理、金融机构与金融市场、国际结算和人寿保险。")]),v._v(" "),_("p",[_("img",{attrs:{src:"//pic.kuaizhan.com/g1/M00/9D/DE/CgpQU1kv-HuASC_IAAAAKwBVcbI6529036",alt:"2{83E2TS77{I(@39BJEK"}})]),v._v(" "),_("p",[v._v("四、毕业去向")]),v._v(" "),_("p",[v._v("至于投资学毕业去向，官方给出的说法如下：")]),v._v(" "),_("p",[v._v("第一，到证券、信托投资公司和投资银行从事证券投资，如投资公司、上市公司、证券公司、信托公司、风险投资公司、商业银行、保险公司等。")]),v._v(" "),_("p",[v._v("第二，到一些社会的投资中介机构、咨询公司、财务公司、基金公司、资产管理公司、金融控股公司、房地产公司等，参与操作、协助决策或给予专业建议。")]),v._v(" "),_("p",[v._v("第三，到企业的投资部门从事企业投资工作。")]),v._v(" "),_("p",[v._v("第四，到各大企业的财会或审计部门，税务部门，参与企业的投资策划与决策、财富理财、风险管理与控制工作。")]),v._v(" "),_("p",[v._v("第五，到政府相关部门从事有关投资的政策制定和政策管理，或者是到事业单位，如会计师事务所及税务师事务所等税务代理机构、政府财税部门，从事行政管理和建议工作。")]),v._v(" "),_("p",[v._v("第六，到高校、科研部门从事教学、科研工作。")]),v._v(" "),_("p",[v._v("辅导员在大一上学期则给我们展示了上财前几年投资学本科生的毕业去向。我记得不是特别清楚，只能给出大概的数据——在已知去向的人当中，只有1/4的本科毕业生选择直接工作，他们主要是去银行、会计师事务所、投资公司等；1/4在国内读研；剩下1/2选择出国深造。")]),v._v(" "),_("p",[v._v("五、学校氛围")]),v._v(" "),_("p",[v._v("到学校之前听说财大考证的氛围很浓厚，或许是因为我才读了大一，所以并没有这样的感觉。可能是因为3:7的男女比例，在我们大一上学期的成绩单上，专业前10有7个女生，前三被女生包揽。我所在的宿舍就占了第一、第五和第七的位子。但是我宿舍中没有谁让我觉得是只会学习的人，每个人都加了学生会，而且现在都在准备竞选下一届各个部门的部长。专业第一此刻就在我旁边写竞选部长的工作计划。学习氛围的话，现在不像高中一样，学习都是大家在一间教室一起学，所以要学习氛围的话，就经常跑图书馆吧，不过图书馆找位置总是一门玄学。")]),v._v(" "),_("p",[v._v("辅导员让我们大一上学期选课不要选太多，这样绩点比较高，对转专业和选择第二专业都比较好，但是这样的后果就是大一下学期课多到爆炸。这个学期可以说是看到很多为了赶各种论文或者活动的ddl熬夜的同学了。所以这种选课方式，第一学期确实很爽，第二学期就有些一言难尽。")]),v._v(" "),_("p",[v._v("六、总结")]),v._v(" "),_("p",[v._v("其实我也只是比看到这篇文章的各位早一年进入大学而已，所以除了自己这一年的感受和辅导员的一些数据外，也只能说一些官方的东西了。另外就是自学的重要性在大学更明显了，大学也不止学习一件事（当然我知道我们南开的同学从来就不止学习一件事），财大为了让同学多参加课外活动，还弄了个第二课堂，也是占了学分的（通过参加社团活动、当志愿者、听讲座等方式拿学分）。所以大家也不要觉得到了大学就比高中轻松了，对我个人来讲，真是比高三还累。")]),v._v(" "),_("p",[v._v("不管怎样，欢迎学弟学妹们报考财大，报考投资学。")])])}),[],!1,null,null,null);_.default=a.exports}}]);