(window.webpackJsonp=window.webpackJsonp||[]).push([[296],{484:function(v,_,t){"use strict";t.r(_);var p=t(7),s=Object(p.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("h1",{attrs:{id:"应用数学系"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#应用数学系"}},[v._v("#")]),v._v(" 应用数学系")]),v._v(" "),_("blockquote",[_("p",[v._v("本文于2013年录入/高2011级张栖侠")])]),v._v(" "),_("p",[v._v("前言")]),v._v(" "),_("p",[v._v("财大应用数学系分为“数学与应用数学”“信息与计算科学”两个专业。每个年级两个专业共有约50人。由于两个专业大同小异，大一大二的学科的基础专业课相同，而且在一起授课，所以就以数学系为整体进行介绍。")]),v._v(" "),_("p",[v._v("第一就业前景")]),v._v(" "),_("p",[v._v("财大学生大学毕业就业大同小异，除非能力很强的人，大多在银行，四大等金融机构从事基础工作。总的来说数学系的就业，就我所了解，并不比其他学院差。")]),v._v(" "),_("p",[v._v("第二考研")]),v._v(" "),_("p",[v._v("数学系考研应该是比较有优势的。因为平时都在学，所以等于不用复习考研里数学那一科。由于高端的金融，经济行业需要用到很深的数学知识，此类学院偏好有着数学本科背景的毕业生。")]),v._v(" "),_("p",[v._v("第三财大数学系特色")]),v._v(" "),_("p",[v._v("刚才说到财大的毕业生就业大同小异，原因就是财大任何一个专业都会学基础的经济学与会计学知识。数学系也不例外。比起其他学校偏向于纯数学理论的教学，财大的数学系是偏向于应用的。在财大的财经环境中，由于平时有机会接触其他财经类学院，参与财经类讲座，可以在学习数学专业知识的同时可以对经济学等知识有所了解，至少受到潜移默化的影响。")]),v._v(" "),_("p",[v._v("第四学习")]),v._v(" "),_("p",[v._v("数学系的学习是需要投入相当多的时间与精力的。对于我个人而言，我认为学好高等数学（数学系里的高等数学基础课程是数学分析与高等代数）只靠努力是不够的，必须要有相应的能力。所以推荐从中学里就对数学有着浓厚兴趣的同学或者有着奥林匹克数学背景的同学报考。学好数学意味着需要花很多的时间，即参加学校活动的时间就相应的减少。另外，在数学系取得高绩点并不容易，这就影响了奖学金、转专业等。")]),v._v(" "),_("p",[v._v("第五师资力量")])])}),[],!1,null,null,null);_.default=s.exports}}]);