(window.webpackJsonp=window.webpackJsonp||[]).push([[307],{489:function(v,_,p){"use strict";p.r(_);var e=p(7),t=Object(e.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("h1",{attrs:{id:"医学院"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#医学院"}},[v._v("#")]),v._v(" 医学院")]),v._v(" "),_("blockquote",[_("p",[v._v("本文于2013年录入/高2010级8班崔诗爽高2010级21班胡澜也")])]),v._v(" "),_("p",[v._v("专业介绍：")]),v._v(" "),_("p",[v._v("临床医学八年制")]),v._v(" "),_("p",[v._v("临床医学八年制的教学管理和学位授予等基本要求与临床医学八年制（法文班）相同，学弟学妹可以把两篇介绍结合起来参考。大一和大四及以后的基本信息主要在法文班的介绍，大二和大三的信息则参见本文。")]),v._v(" "),_("p",[v._v("培养目标：具有医学博士学位（M.D.）的高层次、高素质的医学人才。")]),v._v(" "),_("p",[v._v("就业方向：医院、医药公司、公共卫生组织、家庭医生")]),v._v(" "),_("p",[v._v("专业课详细介绍：")]),v._v(" "),_("p",[v._v("基础课程政治、体育、外语、高等数学、物理、化学、生物学、人体解剖学、组织胚胎学、医学遗传学、生理学、生物化学、病理解剖学、病理生理学、微生物学、寄生虫、药理学等。（理科基础学习阶段在上海交通大学本部学习）")]),v._v(" "),_("p",[v._v("大二")]),v._v(" "),_("p",[v._v("课程名称：医学遗传与胚胎发育")]),v._v(" "),_("p",[v._v("课程内容：由医学遗传、胚胎发育及分子生物学所组成的整合课程。该课程力图从群体、个体、细胞和分子水平阐释人类遗传性疾病的遗传规律、分子基础和发病机制，为遗传病的诊断、治疗和预防提供理论依据。该课程由三个部分组成。第一部分为医学遗传与胚胎发育基础，内容包括人类基因组学；DNA的复制、转录、翻译和调控；胚胎早期发育。第二部分为医学遗传学原理，包括人类染色体；单基因遗传；多基因遗传/复杂性状遗传；群体遗传；表观遗传和临床遗传。第三部分为人类遗传性疾病，包括染色体病；单基因遗传病（生化遗传病）；多基因/复杂性状遗传病；线粒体基因病；体细胞遗传病（肿瘤）。")]),v._v(" "),_("p",[v._v("学习目标：掌握“医学遗传与胚胎发育”的基础理论、基本知识和基本技能，提高自主学习能力、科学思维能力和综合分析能力。")]),v._v(" "),_("p",[v._v("笔者看法：这门课程还是蛮有趣的，只是内容蛮杂的。生物化学部分比较难，不过老师的ppt给的很有调理，医学遗传的内容跟高中比要深入很多，胚胎发育比较难理解，不过老师讲得非常形象。最后会有一次pbl，以问题为中心的讨论课，算是临八的特色吧。最后考试其实很简单，老师会给大家一张样卷的，重复率很高哟")]),v._v(" "),_("p",[v._v("使用教材：陈竺主编，《医学遗传学》（第一版），人民卫生出版社，2005；")]),v._v(" "),_("p",[v._v("成令忠、王一飞、钟翠平主编，《组织胚胎学——人体发育和功能组织学》，上海科学技术文献出版社，2003；")]),v._v(" "),_("p",[v._v("陈诗思主编，《医学生物化学》（第二版），科学出版社，2004。")]),v._v(" "),_("p",[v._v("课程名称：分子细胞与组织")]),v._v(" "),_("p",[v._v("课程内容：以医学分子细胞生物学知识为主干，整合了组织学和生理学中相关内容，从分子、细胞、人体组织和生理学角度阐释人体是怎样从分子到细胞、到组织逐个层次有机地构成的。其中，生化和分子生物学部分阐述细胞的化学组成以及生物大分子的化学特性、结构与功能。在此基础上，细胞生物学部分阐述这些分子如何在特定的细胞亚显微区室形成特定的结构和执行特定的功能；细胞之间及与环境如何发生联系如信号转导；以及细胞有哪些生命活动如细胞增殖和死亡。在“细胞连接、黏附和细胞外基质”一章介绍了细胞如何形成组织之后，组织学部分介绍上皮组织、结缔组织、肌肉组织和神经组织四个基本组织的结构与功能以及主要人体脏器的结构特征，凸显了细胞和细胞外基质在人体中不是简单堆砌而是构成组织才能执行其功能。在“细胞内外的小分子跨膜运输”和“细胞信号转导”两章介绍了细胞如何通过离子运输改变电兴奋性以及如何与外界通讯之后，生理学部分介绍细胞的电性质以及神经对肌肉和腺细胞功能的调控，凸显了神经冲动传导和肌肉收缩这样的人体重要生理活动的细胞和分子基础。本课程是一门跨学科、综合性的新型医学基础课程。")]),v._v(" "),_("p",[v._v("课程目标：掌握细胞结构与功能的基本知识和专业英语词汇，并能够理解分子与细胞的关系、细胞与组织和器官的关系、细胞与人体基本生理活动的关系")]),v._v(" "),_("p",[v._v("笔者看法：这是一门很纠结很纠结的课程。课程名各种整合，然后各种听不懂。特别是讲信号转导的部分，大家基本上都没听懂。电生理的时候请了一个外教来上，但是他的内容是十分重要的，是唯一的一道大题。而且课程当中会有4次小测试。算是大二上学期比较重点的一门课程吧。复习很重要。")]),v._v(" "),_("p",[v._v("使用教材：易静、汤雪明主编，《医学细胞生物学》，上海科技出版社，2009")]),v._v(" "),_("p",[v._v("陈诗书主编，《医学生物化学》，科学出版社，2004")]),v._v(" "),_("p",[v._v("徐晨主编，《组织学与胚胎学》，高等教育出版社，2009")]),v._v(" "),_("p",[v._v("姚泰主编，《生理学》(八年制规划教材)，人民卫生出版社，2006")]),v._v(" "),_("p",[v._v("课程名称：机体防御与免疫")]),v._v(" "),_("p",[v._v("课程内容：包括基础免疫和临床免疫两个部分，基础免疫学主要论述免疫学的基本概念、免疫器官、组织、细胞和分子；介绍抗原作用下淋巴细胞的激活、分化和效应功能；临床免疫则着重讨论临床疾病的免疫病理机制（本课程仅讨论“超敏反应”一章内容）。")]),v._v(" "),_("p",[v._v("课程目标：掌握机体防御与免疫的基本知识和专业英语词汇")]),v._v(" "),_("p",[v._v("笔者看法：一门很神奇的课，分两个老师来讲。前面一个老师是我见过唯一一个不用ppt的老师，他的教学工具就是板书和投影仪，字写得也不清楚，话也听不懂。但是如果你认真听，你会发现他的思路很清晰，你会获益匪浅的。至于后面一个老师，就很正常了。这个科目的考试呢，是医学院最有名的千年一卷，好好研究一下之前的题目是很有必要的。中间会有2次pbl(problembasedlearning，法文班介绍文字中有解释过)的。")]),v._v(" "),_("p",[v._v("使用教材：金伯泉主编，《医学免疫学》人民卫生出版社，第五版，2008年。")]),v._v(" "),_("p",[v._v("课程名称：代谢与能量")]),v._v(" "),_("p",[v._v("课程内容：首先介绍在代谢中起重要作用的酶和维生素，然后分别阐述糖、脂、蛋白质、核酸的代谢、能量的生成与代谢的调节，最后阐述体温调节与发热。")]),v._v(" "),_("p",[v._v("课程目标：掌握物质代谢与能量代谢的基本知识，并结合外语原版教科书学习相关的术语。为学习其它基础医学和临床医学课程奠定基础。")]),v._v(" "),_("p",[v._v("笔者看法：课程内容不多，三大物质的代谢是重点吧。开始的时候觉得比较复杂，不过只要好好复习，掌握其中的规律就还好了。因为是最先考了一门，可能分数不及其他几门这么可观，但挂科还是不存在的。")]),v._v(" "),_("p",[v._v("使用教材：陈诗书主编，《医学生物化学》，上海科技出版社，2004")]),v._v(" "),_("p",[v._v("姚泰主编，《生理学》（八年制规划教材），人民卫生出版社，2006")]),v._v(" "),_("p",[v._v("陈主初主编，《病理生理学》，人民卫生出版社，2005")]),v._v(" "),_("p",[v._v("课程名称：人体构造")]),v._v(" "),_("p",[v._v("课程内容：按人体器官的功能系统（如运动系统、消化系统、脉管系统、神经系统等），阐述人体正常器官形态结构")]),v._v(" "),_("p",[v._v("课程目标：掌握人体正常形态结构的基本知识和解剖学术语，并结合外语原版教科书学习相关的术语。")]),v._v(" "),_("p",[v._v("笔者看法：分两个班教学，给我们上课的老师很帅很有气场，只是上课的速度有点快，第一次可能无法接收。刚接触是觉得要记的内容很多，一定要认真复习呀，不然只能听课越来越迷茫。至于考试，大家不用担心，老师会给重点的，而且还有传说中的系解十九页。")]),v._v(" "),_("p",[v._v("使用教材：柏树令主编，《系统解剖学》（八年制规划教材），人民卫生出版社，2005")]),v._v(" "),_("p",[v._v("课程名称：细胞与分子生物学实验")]),v._v(" "),_("p",[v._v("笔者看法：这个课程要学一学年，包括细胞学和免疫学两个方面，实验不难，会介绍一些科研的基本方法，为以后进行科研实验做好准备。老师们也都是非常好的。这个课你可以好好学，也可以水过去，但是我还是建议大家认真学一下，毕竟机会难得嘛。要作好预习工作哟。考试形式为最后三次课的实验考和最后的笔试考，最后的成绩都很高的。")]),v._v(" "),_("p",[v._v("课程名称：局部解剖学")]),v._v(" "),_("p",[v._v("课程内容：按照人体的局部分区，研究各区域内的器官和结构的形态、位置、毗邻和层次关系的科学。局部解剖学是解剖学的分科之一，它是在学习了系统解剖学的基础上，通过实地尸体解剖和观察，巩固系统解剖学的知识，为进一步学习的临床课程和临床实践打下良好的基础。因此，局部解剖学是基础医学与临床医学之间的重要桥梁课程。")]),v._v(" "),_("p",[v._v("课程目标：掌握人体各局部主要的形态结构特点及其层次和毗邻关系。完成人体各局部的操作")]),v._v(" "),_("p",[v._v("笔者看法：局解是医学味最浓的一门课了，每十人一组分给一具尸体，他会陪你们度过一个学期的时间，包括最后的考试，尽管他不在，我们对他的感情还是很深的，操作时要保持敬畏的态度，你也可以在开始和结束时心里默默的默哀。像医学院这样能够提供这样条件的真不多，一定要珍惜，站那么长时间可能会很累，不过一定要坚持！每节课之前做好预习工作，特别是自己要做主刀的时候。每次操作后做好实验报告，画图了就不太需要写太多字，彩色的细水笔附近的宝宝文具店有卖。")]),v._v(" "),_("p",[v._v("局解考试分为操作考和期末笔试考，操作考试两次，每次指认三到五个结构，就在自己操作的尸体上，内容大部分在附带的资料上。笔试考时老师也会提供范围的，特别是英文的名词解释，大题每年也都是那么几个。最后的考试分数都很高的，大家放心。")]),v._v(" "),_("p",[v._v("使用教材：王怀经主编，《局部解剖学》（八年制规划教材），人民卫生出版社，2005")]),v._v(" "),_("p",[v._v("课程名称：流行病与生物统计学")]),v._v(" "),_("p",[v._v("课程内容：应用卫生统计学和流行病学等原理和方法，分析环境中主要致病因素对人群健康的影响，以制定防制对策；并通过社区卫生保健服务与公共卫生措施，达到保护健康和促进健康的目标，真正体现整体医学观的实施")]),v._v(" "),_("p",[v._v("学习目标：学习运用预防医学的思维方法，以利于今后的医学实践和医学研究")]),v._v(" "),_("p",[v._v("笔者看法：不难，但是很重要，为以后的循证医学学习，RBL(researchbasedlearning)，甚至科研研究打下基础，切不可轻视。")]),v._v(" "),_("p",[v._v("使用教材：《预防医学》，叶葶葶主编，人民卫生出版社2000年出版")]),v._v(" "),_("p",[v._v("课程名称：预防医学与公共卫生")]),v._v(" "),_("p",[v._v("课程内容：以人群为主要研究对象，用预防为主的思想，针对人群中疾病发生发展规律，运用基础医学、临床医学和环境卫生科学的理论和方法来探讨自然和社会环境因素对人群健康和疾病作用规律")]),v._v(" "),_("p",[v._v("学习目标：完整地认识现代医学的目标，透彻理解健康、健康和疾病的关系。初步认识和掌握预防医学观念、知识和技能，牢固树立预防为主思想，培养良好医德。明确社区卫生服务是全球卫生服务的发展趋势。")]),v._v(" "),_("p",[v._v("笔者看法：学的时候感觉不是很难，复习的时候才知道困难，最后的成绩不是很高。这门课，我只想说顺其自然吧。")]),v._v(" "),_("p",[v._v("使用教材：《预防医学》，叶葶葶主编，人民卫生出版社2000年出版")]),v._v(" "),_("p",[v._v("课程名称：病理学与病理生理学总论")]),v._v(" "),_("p",[v._v("课程内容：着重介绍组织细胞的适应、损伤和修复，细胞增殖、分化、凋亡异常和细胞信号转导异常与疾病的关系，以及局部血液循环障碍、应激、炎症和肿瘤等内容。")]),v._v(" "),_("p",[v._v("课程目标：掌握疾病与基本病理过程的主要类型、特点和变化规律，并结合外语原版教科书或外语文献学习相关的术语。")]),v._v(" "),_("p",[v._v("笔者看法：这门可只是学习病理和病生的总论，之后的个论是在后面的系统整合课程里面学的，但总论部分是非常重要的，是个论的基础。总体来说这门课很难，而且八年制教材更难，有些病生的课上课也听不太懂。有几次病理切片实验，就是用显微镜观察切片，有心的同学可以带着U盘将自己拍的照片存下来。会有2次，都还不简单，要用心做哟。关于考试，名词解释和填空空的英文还是不少的，一定要参考学长学姐留下的之前的考试题目。总之，这是一门要用心学、用心复习的学科，不单单是为了刷分。")]),v._v(" "),_("p",[v._v("使用教材：陈杰、李甘地主编，《病理学》（8年制及7年制规划教材），人民卫生出版社，2005")]),v._v(" "),_("p",[v._v("陈主初主编，《病理生理学》（8年制及7年制规划教材），人民卫生出版社，2005")]),v._v(" "),_("p",[v._v("课程名称：药理学总论")]),v._v(" "),_("p",[v._v("课程内容：药理学总论部分，内容包括绪论、药效学、药动学和合理用药原则，还会上一次外周神经系统的药物。")]),v._v(" "),_("p",[v._v("课程目标：掌握药效学和药动学的基本理论、基本知识和基本技能，指导临床正确合理用药，从而达到防治疾病的目的。")]),v._v(" "),_("p",[v._v("笔者看法：药理学总论一共也就9课时，大家看到考试题，也就是一些概念什么的，内容不多。只是名词解释要中译英。重点是前面的总论部分，要学透呀。")]),v._v(" "),_("p",[v._v("课程名称：病原生物学")]),v._v(" "),_("p",[v._v("课程内容：由原核微生物、真核微生物和非细胞型微生物三个部分组成，每一部分又分为总论和各论两部分，前者主要介绍原核微生物、真核微生物和非细胞型微生物在形态结构，生长繁殖，遗传变异等生物学性状，病原微生物和宿主机体的相互关系以及特异性诊断和防治措施等方面的共同特点和原则，在学习各论时有指导意义。而后者具体介绍了与临床感染性疾病密切相关的每一个病原微生物上述各方面的特点，对今后医学课程的学习和临床防病治病具有基础和实际应用意义。")]),v._v(" "),_("p",[v._v("课程目标：掌握上述病原生物各方面的基础理论、基本知识和基本技能，特别要求注重病原体的生物学与临床诊断，治疗和预防相关的理论和实践的联系，为进一步学习其他各门医学基础课程和临床基础课程打下基础。同时要加强对专业外语的学习。")]),v._v(" "),_("p",[v._v("笔者看法：微生物内容很多，很多细节和需要记忆的地方，小黄书的内容几乎全覆盖的，它后面的复习题很有价值的。这门课的实验部分放在大三上，成绩也是合并PBL、实验成绩、寄生虫成绩算在一起的，最后的学分很高。会有3次PBL要求每节课每位同学都要参与进去，最少提一个问题，可以提前最好准备的")]),v._v(" "),_("p",[v._v("使用教材：郭晓奎主编，《病原微生物学》（“十一五”国家级规划教材），科学出版社，2007")]),v._v(" "),_("p",[v._v("课程名称：马克思主义哲学基本原理")]),v._v(" "),_("p",[v._v("课程内容：对学生进行比较系统的马克思主义哲学基本原理教育")]),v._v(" "),_("p",[v._v("课程目标：帮助学生准确地把握辩证唯物主义和历史唯物主义的基本观点，确立辩证唯物的物质观和意识观、唯物辩证的发展观、科学的认识论和真理观、唯物辩证的历史观，使他们能够科学地理解毛泽东哲学思想、邓小平哲学思想特别是“三个代表”重要思想的哲学基础，树立马克思主义的世界观、人生观和价值观，提高理论思维水平和运用马克思主义科学世界观、方法论观察和分析问题的能力。通过理论联系实际的教学为学生树立中国特色社会主义共同理想打下扎实的哲学理论基础。")]),v._v(" "),_("p",[v._v("笔者看法：上课内容和课本几乎没有关系，大部分是哲学内容。考试开卷。上课发言老师会加分的。")]),v._v(" "),_("p",[v._v("使用教材：（已经找不到了）")]),v._v(" "),_("p",[v._v("大三上")]),v._v(" "),_("p",[v._v("课程名称：循环系统；神经系统；消化系统")]),v._v(" "),_("p",[v._v("课程内容：循环系统、神经系统和消化系统的发生、组胚、生理、病理、病生、药理、影像和诊断")]),v._v(" "),_("p",[v._v("学习目标：根据不同要求，掌握、熟悉、了解相关课程内容，塑造系统的整体观念，能在PBL和考试的案例中进行应用")]),v._v(" "),_("p",[v._v("笔者看法：这三个系统是八大系统里内容较多、难度较大的。循环系统心电图很难，但考试时占的分数很少。神经系统解剖是重点，和系解、局解相比神经解剖难度最大，但大家学习和复习的时候也要有信心，难一难就过去了。消化系统要注重英语，考试时会有大题英文出题并要求英文回答，名词解释也可能要求英文作答（09级英文10级中文）。在学习的过程中，以系统为轴线，学习《生理学》《病理学》《病理生理学》《临床诊断学》未学的内容（大三下的过关考会有解剖、生理、病理和生化四门科目的考试），但要注重系统的整体观念。也就是说，可能考试复习的教材是《XX系统》，但PBL或者需要深入查阅某方面的资料时用的是八年制的绿皮书。《临床诊断学》不少内容是分在各系统讲的，但它在临床应用中非常重要。学习系统时要认真，不然PBL时会无话可讲，与临床思维的差距在哪里、自己有哪些需要努力的地方，在PBL的时候大家可以感受到。")]),v._v(" "),_("p",[v._v("使用教材：《循环系统》《神经系统》；《消化系统》")]),v._v(" "),_("p",[v._v("参考书目：《生理学》《病理学》《病理生理学》《影像学》《临床诊断学》等")]),v._v(" "),_("p",[v._v("课程名称：实验诊断学")]),v._v(" "),_("p",[v._v("笔者看法：《实验诊断学》的教材课堂上肯定是不会全部上完的，并且由于实验室检查手段更新迅速，课本上的内容不少都是过时的，所以不管是上课还是复习都是以老师的PPT为主。教材可以作为PBL时的查阅资料。由于没有系统学习基础课程，上课时涉及到生理部分内容（比如黄疸）时可能不懂，但老师会简单讲一下，也可以自己去看《生理学》。")]),v._v(" "),_("p",[v._v("使用教材：《实验诊断学》，老师的PPT")]),v._v(" "),_("p",[v._v("课程名称：病原微生物（寄生虫部分）")]),v._v(" "),_("p",[v._v("课程内容：蠕虫、原虫、节肢动物")]),v._v(" "),_("p",[v._v("学习目标：至少学习指导上的全背下来吧")]),v._v(" "),_("p",[v._v("笔者看法：教材与上课是按虫子来纵向讲解，考试很多是按某种性质横向总结进行考察，比如可以引起贫血的寄生虫有哪些。里面的不少虫子在以后的系统或临床课程中还会涉及，到时忘记了也可以拿出教材来翻阅。学习时要注意将虫子的形态和性质联系起来记忆。授课的老师很有激情，上课记忆效果不错。")]),v._v(" "),_("p",[v._v("使用教材：病原微生物学+学习指导+英文教材（自学）")]),v._v(" "),_("p",[v._v("课程名称：医学影像学")]),v._v(" "),_("p",[v._v("课程内容：总论，骨骼和肌肉系统，胸部，腹部，中枢神经系统与头颈部，介入放射学，超声影像")]),v._v(" "),_("p",[v._v("笔者看法：影像学的要求一共分三等，每等又分甲乙丙三个层次，我们所要达到的要求比三丙要低，所以看不懂图是蛮正常的现象。")]),v._v(" "),_("p",[v._v("使用教材：《医学影像学》，瑞金医院编写的黑皮书纲要")]),v._v(" "),_("p",[v._v("课程名称：临床诊断学")]),v._v(" "),_("p",[v._v("课程内容：总论+症状·发热（其余症状在各个系统里讲）")]),v._v(" "),_("p",[v._v("学习目标：如果是考试的话，掌握、熟悉、了解课程相关内容")]),v._v(" "),_("p",[v._v("笔者看法：诊断学内容在临床应用中非常重要，以后各门科目的PBL也都会用到。不要局限于考试内容，早晚都需要学透。操作考在大三下。")]),v._v(" "),_("p",[v._v("使用教材：《临床诊断学》")]),v._v(" "),_("p",[v._v("大三下")]),v._v(" "),_("p",[v._v("课程名称：内分泌系统；呼吸系统；血液系统、泌尿系统、生殖系统")]),v._v(" "),_("p",[v._v("课程内容：剩余的五大系统的发生、组胚、生理、病理、病生、药理、影像和诊断")]),v._v(" "),_("p",[v._v("学习目标：根据不同要求，掌握、熟悉、了解相关课程内容，塑造系统的整体观念，能在PBL和考试的案例中进行应用")]),v._v(" "),_("p",[v._v("笔者看法：参见上学期系统课程。生殖系统以组胚为重点，它的PBL是妇产科的。其余基本都以生理、病生为重点。")]),v._v(" "),_("p",[v._v("使用教材：《内分泌系统》《生物化学》；《呼吸系统》；《血液系统》；《泌尿系统》；《生殖系统》")]),v._v(" "),_("p",[v._v("参考书目：《生理学》《病理学》《病理生理学》《影像学》《实验诊断学》《临床诊断学》《妇产科》等")]),v._v(" "),_("p",[v._v("课程名称：外科学，妇产科学")]),v._v(" "),_("p",[v._v("笔者看法：从这两门课开始内外妇儿临床课程的学习。老师上课会穿插各种临床上的故事，非常有趣。重点是各种疾病的基本诊断要点，发病机制，临床表现，治疗原则以及指证与禁忌症。见习很多，跑六院和仁济很累，遇到好的带教老师会觉得受益匪浅。")]),v._v(" "),_("p",[v._v("使用教材：《外科学》《妇产科学》")]),v._v(" "),_("p",[v._v("本文于2013年录入/高2012级3班岳雨")]),v._v(" "),_("p",[v._v("学术氛围：没有疑问，医学院的学术氛围是很浓厚的。甚至在食堂的楼梯上都遇到学长们讲开刀位置的问题……选择交大医学院就是选择了责任与奉献，学术，很重要。偶尔，你会发现自己又进入了高三的状态，投入，纯粹。")]),v._v(" "),_("p",[v._v("活动氛围：虽然卢湾校区很小，但是麻雀虽小，五脏俱全。可以说，有你爱的，有你愿意为之投入的，就有属于你的活动。比起闵行校区来，我们校内的活动确实没有那么多彩盛大，但是别忘了我们是在市中心——北有新天地，南有田子坊，各种美术馆、纪念馆近在咫尺，需要的是你那一双发现的眼睛。")]),v._v(" "),_("p",[v._v("学院专业：临床医学八年制（法文班）")]),v._v(" "),_("p",[v._v("专业介绍：")]),v._v(" "),_("p",[v._v("临床医学八年制（法文班）")]),v._v(" "),_("p",[v._v("这是中法两国政府为加强中法交流的重点合作项目之一，也是交通大学医学院和法国医学院校之间历史久远的高水平合作的延续。该项目受到了两国政府有关部门的高度重视和支持。学生完成规定的学业后，经考核合格者被选送赴法国的医院，作为实习住院医师培训一年，由法国医学院校授予“专业培训证书”。")]),v._v(" "),_("p",[v._v("培养目标：具有医学博士学位（M.D.）的高层次、高素质的医学人才。")]),v._v(" "),_("p",[v._v("【注：我国现行的医学学位设科学学位和专业学位两种。临床医学八年制毕业生授予医学博士（M.D.）专业学位】")]),v._v(" "),_("p",[v._v("就业方向：医院、医药公司、公共卫生组织、家庭医生")]),v._v(" "),_("p",[v._v("专业课详细介绍：")]),v._v(" "),_("p",[v._v("课程名称：法语")]),v._v(" "),_("p",[v._v("课程内容：法语的教学分为三个板块：外研社版《法语（LeFrançais）》课（基础、语法），引进教材《走遍法国（Reflets）》课（日常用语），外教口语课。还有一个“第二课堂”——法语角。外教人都很好，会想出各种花样办法语角的活动。比如之前请来过在法国里昂当医生的某先生一家人，还请过在法国学习糕点制作的一群年轻人教我们做点心，另外，每年的法语角办的圣诞晚会都很high!")]),v._v(" "),_("p",[v._v("学习目标：从大一下学期开始，我们的基础课、专业课就有相当一部分（基础化学、有机化学、组织胚胎学等等）用法语教学，考试了。因此从第一个学期开始，法语的基础就必须打好，这样后续的课程才能够学习得比较顺畅。我们在大七时有50%左右的机会去法国医院实习一年。（有欧元工资领的哦~之前有一场法方的考试，通过率会有浮动。）所以我们的目标是，在法国实习期间，拥有能与医生、护士工作，和病人们流利地交流的水平。")]),v._v(" "),_("p",[v._v("笔者看法：这个专业大一期间最重要的一门课就是法语，课时与其他所有课程加起来差不多。而语言学科需要下的功夫也是很多的（不过个人感觉在学习过英语后，法语上手并不难，脑力心力与英语要求的差不多），因此对语言有一定兴趣的人比较适合来这个专业。这几年出现过有个别同学因为法语方面的障碍而转到其他专业的情况，所以希望学弟学妹们做好心理准备，考虑清楚这个问题。其实学法语也是一件很幸福的事，你会渐渐发现，一个语言它所带来的一系列东西很fascinating，朋友、文学、美食、行走、科技、意识……")]),v._v(" "),_("p",[v._v("使用教材：《法语（LeFrançais）》（外研社版）《走遍法国（Reflets）》（外研社版）")]),v._v(" "),_("p",[v._v("课程名称：基础化学、有机化学")]),v._v(" "),_("p",[v._v("课程内容：一些比较基础的大学化学知识。前面提过，法语教学会占一定比例，不过别担心，老师会先用中文解释反应机制等等需要动脑的内容，所以理解方面没有问题。其实，取代反应、苯环这些词用法语说起来还是很有意思的。")]),v._v(" "),_("p",[v._v("大二到大四专业课：公共基础课程（略）")]),v._v(" "),_("p",[v._v("医学基础课程:医学细胞生物学神经科学基础人体解剖学组织胚胎学生理学生物化学医学免疫学病原生物学医学心理学病理学法医学病理生理学药理学医学遗传学医学统计学预防医学细胞与分子生物学实验功能实验循证医学基础医学PBL（PBL:ProblemBasedLearning有些类似seminar研讨课）")]),v._v(" "),_("p",[v._v("临床医学课程:诊断学实验诊断学医学影像学外总外科手术学内科学外科学妇产科学儿科学神经病学精神病学核医学康复医学传染病学中医学眼科学耳鼻咽喉科学皮肤病与性病学临床医学PBL")]),v._v(" "),_("p",[v._v("大五：52周的临床通科实习")]),v._v(" "),_("p",[v._v("大六到大八：导师安排专业课程、临床二级学科培养轮转、学位论文、毕业综合考试")]),v._v(" "),_("p",[v._v("笔者看法：有学长说，医学是所有大学专业中最累的一类。毕竟我没有体会过其他专业，无法定言。但是，在学医过程中会有很多的辛苦、挑战，乃至对自己未来的质疑等等，是无疑的。不过，面对这些，最好的解决方式，在我看来就是认清自己。想清楚，自己对这一系列要学的东西，有没有一种热爱，有没有和自己兴趣的契合处。如果有的话，无论在多大的压力下，你也不会失去自我，这样就好。另外，比较实际一点的tips也有一些：")]),v._v(" "),_("p",[v._v("关于临五与临八的选择问题：如果一概地说临八比临五好，其实是不客观的。临八的好处在于拿到博士学位需要花的时间比较短，但是如果毕业后想留在上海做医生的话，还是需要有1~3年的住院医师规范化培训，在那期间确实有种充当廉价劳动力的感觉。临五虽然需要一步步地考研、考博，但是学得自然更扎实，而且在毕业前有直研名额（20%左右），还会有外推名额，另外还有一部分的人能够成为“基地研究生”（20%左右，在基地的三年完成住院医师规范化培训，之后拿医学专业硕士学位）。不过，政策方面的问题，一直是随时间变化的，这只能作为你你进行选择时一部分的参考。")]),v._v(" "),_("p",[v._v("关于选复旦医学院还是选交大医学院的问题：（如果做比较的话，观点倾向于个人化，不客观之处可能有些多，大家慎重参考）从大学文化活动方面来说，首先复旦医学院与复旦大学的融合程度肯定是高于交大这边的，复旦校本部与医学院交流比较密切，因此医学院的活动会更倾向于综合化，相对于交大医学院会更有人文气息。交大医学院和校本部比较脱离，没有来往的固定校车，因此校内专业同质化的感觉可能让人有点憋闷。从校园来说，复旦医学院更像一个普遍意义上的大学——校园更大，生活气息更浓，周围环境热闹而日常。而上交医学院比较有特点——校园比较小，但比较精致，处在早期法租界的中心地带，周围有很多“新式里弄”、教堂、旧式公寓等等比较有欧式风情的建筑。上交医学院一带的气质比较“摩登”，复兴中路的思南公馆有很多露天咖啡馆，田子坊有一些艺术家的工作室。不过，在这里可能会觉得这些校外繁华并不是为学生所设的，甚至有些地方会被形容为没有必要的“奢侈”。从专业水准上说，上交医学院的临床专业实力很强，专业排名全国第一，附属医院的数量更多，而复旦医学院的科研水平较高，基础研究很好。")])])}),[],!1,null,null,null);_.default=t.exports}}]);