(window.webpackJsonp=window.webpackJsonp||[]).push([[308],{490:function(v,_,p){"use strict";p.r(_);var t=p(7),n=Object(t.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("h1",{attrs:{id:"经济学类"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#经济学类"}},[v._v("#")]),v._v(" 经济学类")]),v._v(" "),_("blockquote",[_("p",[v._v("本文于2013年录入/高2012级27（20）班余柳璇高2012级3班蔺诗偲")])]),v._v(" "),_("p",[v._v("学术氛围：整个交大由于工科为主，都属于比较严谨认真的，除韩国留学生，学生专业课逃课或不完成作业情况较少，老师会有一定监督。经济管理学院的专业课相比其他工科院系少很多，也不用学大学物理或者写实验报告，平时比较轻松，被誉为养老院，可以留出很多时间做自己想做的事，但是大部分同学对待专业课都比较认真刻苦，有外专业说我们学院是学霸最多的，在英语方面整体水平略高于其他大部分专业。")]),v._v(" "),_("p",[v._v("活动氛围：学生会会组织一些活动，同时学校也会有各种各样讲座和活动，活动比较多，种类也很丰富，所以学生只会选择部分感兴趣的参加。一般每个学生都加了两个左右学生组织（大一均为干事，之后可竞选）。另外安泰的女生特别多，大概女比男2.5比1。")]),v._v(" "),_("p",[v._v("学院专业：经济学，金融学，国际经济与贸易，会计学（属于管理学院）【大一在闵行校区，大四会搬到市区】")]),v._v(" "),_("p",[v._v("专业介绍：填报经济学类，大一上都是统一不分具体专业的，大一下学期4月份会选择分流，可选择会计学、经济学试点班以及经济学大类。会计学每年最多招50个，但都没报满。在经济学大类和试点班里，学生可以自由选择经济学、金融学和国际经济与贸易，修满相应的学分就可以拿专业证书。经济学试点班招15-30个，一般不会到上限，有一定英语和数学要求，专业课全部采用英文授课，在数学内容上也学得更深，之后也可以修读经济、金融、国际经济与贸易。也就是说，填报经济学类，基本可以自己选择之后的分流方向。本科毕业，会计学获得管理学士学位，其他均获得经济学学士学位。")]),v._v(" "),_("p",[v._v("培养目标：不太清楚，只有经济学试点班有说要培养学术型人才。留经济学类的人多选金融学，少数选经济学（据说比较走学术路线），成绩不好有些选国际经济与贸易（多为留学生）。会计学近年就业非常热门。")]),v._v(" "),_("p",[v._v("就业方向：国内读研，出国留学和工作为主（其中会计学为事务所37%，读研37%，出国13%）金融出国的人会比较多。其中不论哪个专业工作去的最多的地方均为四大会计师事务所，金融经济走银行投行等的也很多。会计、金融和经济毕业时的就业及薪水其实差不多，国贸相比会差一点。（具体数据和资料可以看交大安泰的官网）")]),v._v(" "),_("p",[v._v("专业课详细介绍：")]),v._v(" "),_("p",[v._v("笔者在这里只能说说大一的课程。")]),v._v(" "),_("p",[v._v("课程名称：数学分析")]),v._v(" "),_("p",[v._v("课程内容：就是经济学院要学的大学数学，比较侧重于分析证明。")]),v._v(" "),_("p",[v._v("学习目标：熟悉数学分析的思维模式，为大二的专业课程打基础。")]),v._v(" "),_("p",[v._v("笔者看法：这门课是安泰学生大一的重点课程，占6个学分（一般的课程都是2个学分）。课程的开展方式和高中数学比较接近，老师讲课，下面听课记笔记，回去完成作业。有一定难度，得认真听讲，完成好作业才行。因为大学的数学节奏比较快，内容也很大，所以这门课会考验大家的自学能力还有自觉能力。此外，学院会专门开设习题课。总的来说，高中数学升级版，不过南开的学生在数学方面是有一定优势的。")]),v._v(" "),_("p",[v._v("266")]),v._v(" "),_("p",[v._v("http://ilovecqnk.info/")]),v._v(" "),_("p",[v._v("4.24上海交通大学")]),v._v(" "),_("p",[v._v("使用教材：大学数学数学分析（共两册），上海交通大学数学系数学分析课程组编，高等教育出版社")]),v._v(" "),_("p",[v._v("课程名称：会计学")]),v._v(" "),_("p",[v._v("课程内容：学习会计准则，初步了解会计报表的制作方法")]),v._v(" "),_("p",[v._v("学习目标：了解一些基本的会计知识，能做会计报表")]),v._v(" "),_("p",[v._v("笔者看法：这门课的老师比较用心，能学到些基本的会计学知识。这部分知识对后来的无论是经济、金融方向还是会计方向都会有帮助。")]),v._v(" "),_("p",[v._v("使用教材：会计学企业决策的基础机械工业出版社；")]),v._v(" "),_("p",[v._v("会计学原理—基础会计立信会计出版社（不同班级的老师用的教材不一样）")]),v._v(" "),_("p",[v._v("课程名称：经济学原理")]),v._v(" "),_("p",[v._v("课程内容：学习宏观、微观经济学的基本原理")]),v._v(" "),_("p",[v._v("学习目标：主要是理解经济学的基本原理，宏观微观各十个。")]),v._v(" "),_("p",[v._v("笔者看法：这门课比较水，因为量比较大（两本书一共900多页），老师没有办法详细系统的讲，进度非常快。所以如果想学好这门课，平时得多下功夫。在这里简单说说大一与经济学相关的课程的共性：量大，老师的节奏很快。一堂课讲个几十页书完全不是问题。所以学习经济学一定要注意预习和复习，毕竟老师在课堂有限的时间内只能讲概括性的东西。")]),v._v(" "),_("p",[v._v("使用教材：经济学原理微观经济学分册曼昆著北京大学出版社")]),v._v(" "),_("p",[v._v("经济学原理宏观经济学分册曼昆著北京大学出版社")]),v._v(" "),_("p",[v._v("课程名称：货币银行学")]),v._v(" "),_("p",[v._v("课程内容：研究货币，金融机构，金融市场。")]),v._v(" "),_("p",[v._v("学习目标：了解一些关于货币学关于金融市场的基本概念吧。")]),v._v(" "),_("p",[v._v("笔者看法：一门本来是大二才开的课，但是今年放到大一。拓展性比较强。")]),v._v(" "),_("p",[v._v("使用教材：货币银行学（美国商学院版）弗雷德里克S米什金著机械工业出版社")]),v._v(" "),_("p",[v._v("课程名称：金融学")]),v._v(" "),_("p",[v._v("课程内容：课堂教学，教授金融行业的基本概念，课下调查企业报表，小组活动等等")]),v._v(" "),_("p",[v._v("学习目标：了解金融学方面的基本概念，了解金融行业如何进行数据分析等等")]),v._v(" "),_("p",[v._v("笔者看法：这门课程使用全英文教材，很厚，但易懂。学院开设了中文和英文教学班，学生可以自行选择。安泰在培养计划上比较侧重于接轨国际，学习的教材多选用欧美教材。所以，对英文的要求会逐步的提高。学习方法和难度，和上面提到的经济学课程有共通性，需要花时间去读书消化，还需要课下逐步培养自己关注经济类新闻的习惯。")]),v._v(" "),_("p",[v._v("使用教材：FoundationsofFinance：TheLogicandPracticeofFinancialManagement（InternationEdition）KeownMartinPetty著")]),v._v(" "),_("p",[v._v("大二到大四：线性代数（A类），概率统计，中级微观经济学，商法，统计学，经济优化方法，中级宏观经济学，国际金融（A类），国际贸易理论与政策，计量经济学，中国经济专题等。注，大二到大四课程会比较多，不同的大类以及不同大类中的具体分流都会有不同课程，所以笔者在这里就不全部展示出来了。具体可以到“上海交通大学教学信息服务网”查询专业培养计划，或者直接给我邮件nklsc@sina.com。")]),v._v(" "),_("p",[v._v("分数线参考：交大经济学类2012级录取两人，665和664，交大分数线663，2011级也是只比分数线高一两分，但是在录取的时候经济学会比其他电院之类的难进一点。复旦经济学收分669，就分而言一般复旦经济学都会比交大经济收分高。交大一般收分最高的专业都为试点班专业。其他在重庆投放了名额的专业都差不多。在重庆经济学收分差不多都是高于分数线一点点，但是在上海本地的报考要求及分数线会比较高，算上海地区比较热门的专业。另外安泰招生也算第三大院，进来的同学基本上不会出现想换专业，但报名转入经济的人一般很多，要求很高。现在报经济学的大多为理科生，文科生数学上会学起相对困难一点。")]),v._v(" "),_("p",[v._v("分数线网址：http://gkcx.eol.cn/schoolhtm/schoolSpecailtyMark/125/schoolSpecailtyMark.htm")])])}),[],!1,null,null,null);_.default=n.exports}}]);