(window.webpackJsonp=window.webpackJsonp||[]).push([[32],{214:function(t,v,_){"use strict";_.r(v);var n=_(7),s=Object(n.a)({},(function(){var t=this,v=t._self._c;return v("ContentSlotsDistributor",{attrs:{"slot-key":t.$parent.slotKey}},[v("h1",{attrs:{id:"我眼中的北理工"}},[v("a",{staticClass:"header-anchor",attrs:{href:"#我眼中的北理工"}},[t._v("#")]),t._v(" 我眼中的北理工")]),t._v(" "),v("blockquote",[v("p",[t._v("作本文于2017年录入/高2014级5班梅笑寒")])]),t._v(" "),v("p",[v("strong",[t._v("学校简述：")])]),t._v(" "),v("p",[t._v("北京理工大学（BeijingInstituteofTechnology），直属中国工信部，是我们俗称的985高校之一。北京理工大学，就像名字一样，偏重于理科、工科的培养教育。2017年，北京理工大学学校综合排名29，QS世界大学排名389位。不过北京理工大学是军工国防院校，有许多项目是国家保密，进不了排名估算。就比如学生进了北京理工大学后，最常见的一句话不是校训“德以明理，学以精工”，而是每次登录校园网都会出现的“上网不涉密，涉密不上网”。")]),t._v(" "),v("p",[v("strong",[t._v("校区制度：")])]),t._v(" "),v("p",[t._v("学校本科生大部分专业前两年在良乡校区，该校区在2007年级建立，各种设施比较崭新，到目前为止，体育馆仍处于施工状态，所以体育设施多是室外的。由于处于北京六环，比较偏远，周围能提供的娱乐活动不算太多，而远离闹市，也杜绝了很多的诱惑，所以学生可以静下心学习，有师兄戏称良乡校区是大学里的高中，我先你们三年，从有高中里的大学之称的重庆南开中学来到北京理工大学良乡校区，当时也感叹际遇之神奇。新生到了大三，会搬回中关村校区，这里的学生由高年级本科生、研究生、博士生组成。北理工中关村校区与北京外国语大学、中央民族大学、中国人民大学毗邻，处于闹市中心，学校被酒吧、KTV以及各种美食包围，人称堕落一条街，交通四通八达，想去哪都很方便。相对住宿条件比良乡校区差。")]),t._v(" "),v("p",[v("strong",[t._v("学校风气：")])]),t._v(" "),v("p",[t._v("学校老师治学严谨，学生学习风气浓郁，学术气息充沛，由于是理工科院校，相比于其他大学，会缺少一些人文气息。（当然学校在不断改善这个问题）")]),t._v(" "),v("p",[v("strong",[t._v("优势学科：")])]),t._v(" "),v("p",[t._v("北理工的优势专业有软件工程、安全工程、地武、车辆、机械、武器、自动化、飞行器设计等等，总的来说偏向国防和军工，拿国庆60周年阅兵举例，北理工参加了30个方阵之中22个方阵的装备设计和研制。")]),t._v(" "),v("p",[t._v("此外一些看起来排名不高的如数学、化学，实际上还是很有实力的，特别是数学学院本来师资力量就不错，这几年势头也很好，进行了不少新的改革，很有朝气。")]),t._v(" "),v("p",[t._v("我就读的环境工程专业在材料学院，材料学院近年来也发展不错，不过我的专业在当年重庆考生中只录取了一人，现在由于学院战略和教学调整，环境工程已经是最后一届，很抱歉无法在专业导向上为你们提供参考。")]),t._v(" "),v("p",[v("strong",[t._v("建议：")])]),t._v(" "),v("p",[t._v("关于学习，起早贪黑如高中之刻苦者有之，浑浑噩噩懒散度日者亦有之。我曾经像打了鸡血一样学习，拿过不少奖学金。也曾经忙于学生工作，当过学生组织的主席，而导致GPA有所滑坡。回顾我的大学时光，给读到这篇充满理工男气息的文字的学弟学妹一点建议：无论你选择去哪个大学，希望你趁早明白，大学不是休息的场所，无论你选择做学生工作还是当一只学霸，无论你早早跟了导师进了实验室还是沉迷美好爱情，不要荒废自己，时间在这里，比高中更宝贵。")])])}),[],!1,null,null,null);v.default=s.exports}}]);