(window.webpackJsonp=window.webpackJsonp||[]).push([[329],{511:function(v,_,p){"use strict";p.r(_);var t=p(7),n=Object(t.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("h1",{attrs:{id:"学校综述一"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#学校综述一"}},[v._v("#")]),v._v(" 学校综述一")]),v._v(" "),_("blockquote",[_("p",[v._v("本文于2013年录入/高2012级3班 潘锦博")])]),v._v(" "),_("p",[v._v("学校概述")]),v._v(" "),_("p",[v._v("中山大学由孙中山一手创立（同时创立的学校为黄埔军校），基本的历史就不罗嗦了，随便百度一下就能查到许多。")]),v._v(" "),_("p",[v._v("学校分为四个校区：位于广州市区的南校区，北校区，位于广州市大学城的东校区，位于珠海市的珠海校区。")]),v._v(" "),_("p",[v._v("南校区一般为文科以及纯理科，学校不大，但十分漂亮，十分精致，环境十分好，自己走去过厦门大学，武汉大学，清华大学，但做比较这几所学校的建筑都不及南校的建筑精致且保留得这么好，南校的很多建筑自从修好后就几乎没重修过，所以很具有历史感。绿化环境很好，很多树三个人围在一起都抱不下。住宿环境不太好，有些宿舍需要去集体澡堂。")]),v._v(" "),_("p",[v._v("北校区是医学校区，校园不大，也是这四个校区唯一有通宵自习室的校区。宿舍环境与南校差不多。")]),v._v(" "),_("p",[v._v("东校区在大学城（距离市区也不远，坐地铁30分钟到最繁荣的市区），东校区与南校区差不多大，但十分现代化，工科校区，宿舍环境很好，有独立卫生间，独立浴室，有空调，有风扇，阳台有洗衣机。东校区的吃得很多，而且很便宜。")]),v._v(" "),_("p",[v._v("珠海校区大多为语言学科，校区很大，而且很漂亮，依山而建，且学校对面就是海。学校挺适合去度假的，某个周末可以去珠海海边玩玩散散心。")]),v._v(" "),_("p",[v._v("四个校区之间有校车，而且校车价格不贵，很方便。")]),v._v(" "),_("p",[v._v("学校管得不严，不查寝室，不断电，不断网。所以一切靠自觉。")]),v._v(" "),_("p",[v._v("学校全国排名2012，2013都居第七。")]),v._v(" "),_("p",[v._v("地理优势")]),v._v(" "),_("p",[v._v("中山大学位于中国第三大城市广州。")]),v._v(" "),_("p",[v._v("在这里的第一地理优势便是可以学习新的语言。在广东，通行粤语，潮汕话，客家话（我寝室说每种话的人一样一个），每种话全世界的使用人数都超过了1亿的，其中粤语最International，目前粤语已经成为澳大利亚第四大语言，加拿大第三大语言，美国第三大语言，香港与澳门的法定语言。在广州其实学粤语很简单，每天早上起来听听粤语的广播，半年内几乎可以听懂一半，和班上的同学交流交流，不久可以说出口，当然要说得像本地人还得花点力气。")]),v._v(" "),_("p",[v._v("而且广州的确是一个文化大熔炉，你会经常看到在一些书店里面，不同肤色的人坐在一起谈论政治，文化。")]),v._v(" "),_("p",[v._v("在广州以后出国也提供了很多方便，大家都知道很多国际的转机都会在广州，而且有些国际的考试也要来广州考。")]),v._v(" "),_("p",[v._v("广州的交通十分方便，放假的时候，买上一张火车票，飞机票，就可以轻松地出去看看。")]),v._v(" "),_("p",[v._v("学术氛围")]),v._v(" "),_("p",[v._v("中山大学作为华南第一名校，学术氛围自然不容小觑。教育部投入广东省的科研经费大部分都流向了这所学校。为中才立规矩,给天才留空间，这是这所学校一直所秉持的理念。学术氛围自由，不管什么样的课题都敢涉及，比如最近网上炒得很红的中山大学女教授组“自慰研究组”。")]),v._v(" "),_("p",[v._v("转专业")]),v._v(" "),_("p",[v._v("在中大转专业并不困难，每学年后只要学分达到一定的指标就可以参加转专业的考试。一般在年级排在30左右的都有资格转专业。只不过转专业的考试你就得花很多心思去准备了，因为考得很多的是该专业的理论，如果从来没涉及就有点恼火。需要从头来学习，但一般给你准备的时间不多。")]),v._v(" "),_("p",[v._v("学业压力")]),v._v(" "),_("p",[v._v("中山大学的学习压力不算大，比起一些理工科学校来讲实在微不足道，学校的考试更多的是考察这些知识点你是否掌握了，而不是很多大学那种选拔式的考试，非要把你考倒为止。")]),v._v(" "),_("p",[v._v("但中山大学比较注重知识的运用，所以对理工科来讲实验看得很重，对文科来讲社会调查看得很重。")]),v._v(" "),_("p",[v._v("公选课")]),v._v(" "),_("p",[v._v("学校的公选课很多，但有些有点水（有的听名字都听得出来很水），但大部分还是能学到知识，当然如果你只想轻松过，有很多选择，但学到的东西也有限，一般好的公选课上的时间长，考试闭卷考。")]),v._v(" "),_("p",[v._v("男女比例")]),v._v(" "),_("p",[v._v("学校男女比例1：1，据网上的资料，全国高校的美女排行，中山大学美女排第三（前面是中国电影学院，重庆大学），中山大学同性恋人数排第二，所以网上的段子：清华毁一生，北大穷三代，要是中大，必成高富帅。学姐会卖萌，学妹很可爱，学长很亲切，学弟天然呆。基友全国来，昼夜都可嗨。男女对半开，取向看心态。教室有风扇，冬天很凉快，着装很自由，拖鞋随便踩。不查房，不早操，不晚自习，不早晚读，如果你心动，报名趁现在！")]),v._v(" "),_("p",[v._v("奖学金")]),v._v(" "),_("p",[v._v("学校的奖学金份额不大，但种类繁多，几乎算人人有份那种，只要你班上排到三分之一以前就有。")]),v._v(" "),_("p",[v._v("社团活动")]),v._v(" "),_("p",[v._v("学校社团很多，号称百团，总有一款适合你。")]),v._v(" "),_("p",[v._v("学校活动也很多，来这里一年我在学校听了一场俄罗斯钢琴家的演奏会，看了一场《骇战》的提前放映，看了《至青春》的新闻发布会，n场演讲。")]),v._v(" "),_("p",[v._v("最让我感到不可思议的是刚刚举行的中山大学第27届维纳斯歌唱比赛，林志颖，林志炫，方大同，金莎，李易峰，曹轩宾，张芸京，许哲佩，陈妍希各自为比赛录了祝福。")]),v._v(" "),_("p",[v._v("PS:中山大学真的很自由，你即使一个星期不来上课都没人管你，准确的说是没人知道，所以一切都要靠自己，要自觉。这种宽松的环境既成就了许多人，同时也毁了一些人。")])])}),[],!1,null,null,null);_.default=n.exports}}]);