(window.webpackJsonp=window.webpackJsonp||[]).push([[373],{555:function(v,_,p){"use strict";p.r(_);var t=p(7),s=Object(t.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("h1",{attrs:{id:"国际商学院"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#国际商学院"}},[v._v("#")]),v._v(" 国际商学院")]),v._v(" "),_("blockquote",[_("p",[v._v("本文于2013年录入/高2012级20班王茜")])]),v._v(" "),_("p",[v._v("学术氛围：我感觉吧，整体的学术氛围不是很浓厚。要说研究的话，我觉得这跟个人有关系，如果你有能力做学术研究的话，商学院也能给你提供机会，像各种商业知识大赛，挑战杯什么的。至于学习方面，作为三大院之一，商院学习氛围也还算不错，常有学霸出没。")]),v._v(" "),_("p",[v._v("活动氛围：提到活动，我觉得这算是商院的一大特色了。商学院有着充足的活动经费，因而活动大都反响比较好，参与度很高。比如说：去年院庆的时候，商院就直接在某教学楼的大厅里搞“嘉年华”活动，弄来各种电玩设备供学生玩耍，场面之热闹，你可想象啦。")]),v._v(" "),_("p",[v._v("学院专业：会计，财务管理，工商管理（全球实验班），工商管理（兰斯方向）【这个专业在重庆不招生】，市场营销，人力资源。")]),v._v(" "),_("p",[v._v("专业介绍：")]),v._v(" "),_("p",[v._v("财务管理")]),v._v(" "),_("p",[v._v("培养目标：本专业旨在培养德智体美全面发展，具有良好职业道德和社会责任感，熟悉本国及欧、美等主要国家的金融环境和金融法规，具备财务管理、会计、金融以及企业管理方面的专业知识，具有实际操作能力和创新意识、跨文化沟通能力和团队协作精神，能在企事业单位、金融机构及政府部门等各类单位中从事财务管理、会计等相关方面工作的国际化、复合型高素质专门人才。")]),v._v(" "),_("p",[v._v("就业方向：去国企、外企的比较多，像四大等，从事会计、管理等工作。其实到底能进到什么样的企业，这还是跟个人能力、学得好与坏有极大关系的。")]),v._v(" "),_("p",[v._v("专业课详细介绍：")]),v._v(" "),_("p",[v._v("我们大一还没学习专业课，学的多是公共基础课程，专业课要到大二才开始涉及（整个商院都是如此）。现在正学习的是《微观经济学》和《财务会计》，稍微和财务、经济挂点钩，但是这两门课貌似是贸大每个专业的学生都必须学的。")]),v._v(" "),_("p",[v._v("课程名称：《微观经济学》")]),v._v(" "),_("p",[v._v("课程内容：主要是学习消费者、生产者和市场等基本概念及其相互之间的关系，研究需求、供给、均衡价格，效用论，生产论，成本论，完全竞争市场，不完全竞争市场，生产要素价格的决定，一般均衡论和福利经济学，博弈论初步，市场失灵和微观经济政策。")]),v._v(" "),_("p",[v._v("学习目标：能独立地分析市场中的经济规律、经济现象，能将所学的经济知识与现实有机地结合起来。")]),v._v(" "),_("p",[v._v("笔者看法：这是一门比较有趣的课程，通过这门课可以学习到一些经济学知识，有些自己还能用来解释生活中的经济现象，还能对管理决策的制定，公共政策的有一定的涉及和了解，对于现代经济运行规律有一个初步的认识，我觉得还是挺有用的。")]),v._v(" "),_("p",[v._v("使用教材：在贸大，有两个学院开这门课，这两个学院使用的教材也略有不同。选国际经济贸易学院的老师的话，用的是平狄克版《微观经济学第七版》中国人民大学出版社；而金融院用的是平狄克版《微观经济学第七版》和高鸿业版《西方经济学第五版》中国人民大学出版社。")]),v._v(" "),_("p",[v._v("课程名称：《财务会计》")]),v._v(" "),_("p",[v._v("课程内容：财务会计的基本理论，经济业务分析与复式记账原理，会计调整和损益的确认计量，商品交易和商业企业的会计业务，流动资产，长期营业用资产，企业负债，股东权益，企业对外投资，现金流量表，财务报表分析的基本方法。")]),v._v(" "),_("p",[v._v("笔者看法：学习基本的财务知识，主要学习借贷记账法，搞清楚涉及会计事项时应记哪个账户，究竟是记贷方还是借方就可以了，而且考试的时候据说是可以用计算器的。")]),v._v(" "),_("p",[v._v("使用教材：《财务会计》主编余恕莲出版社：对外经济贸易大学")]),v._v(" "),_("p",[v._v("大二到大四专业课：")]),v._v(" "),_("p",[v._v("学科基础必修课：财务会计，管理学原理，货币银行学导论，营销学原理，公司理财，战略管理，商业伦理。")]),v._v(" "),_("p",[v._v("学科基础选修课：企业信息管理，财政学，企业风险管理，审计学，银行会计学等（可选的实在太多了）")]),v._v(" "),_("p",[v._v("专业方向必修课：成本与管理会计，企业财务报表分析，国际财务管理，证券投资管理，金融时间序列分析，投资银行理论与实务，高级财务管理，财务工程概论")]),v._v(" "),_("p",[v._v("分数线参考：2011年：理636文6362012年：理641文639")]),v._v(" "),_("p",[v._v("其他专业")]),v._v(" "),_("p",[v._v("笔者看法：对于商院的其他专业我就谈谈我说知道的吧。在大一时，商院除工商管理专业外其他的的专业学的课程都是一样的，多为公共基础课程，并未涉及专业课。工商管理这个专业，我建议文科生慎报，大一上学的是《数学分析1》（工科数学），大一下学得是两门数学课程《数学分析2》和《线性代数》，所以这个专业对数学还是有比较高的要求。在大一下时，该专业的学生还学习《管理学原理》（原版英文教材）。从大一来看，相对于商院的其他专业，工商管理的学生学业还是比较重的。从进入商学院的分数来说，最高的是会计，其次是财务管理，接下来是工商管理，市场营销和人力资源这两个专业的分差不多。我问过很多的学长学姐，会计和财务管理有什么差别，他们说学的可能都差不多，都是想通的，会计学得深一些，财务管理学得要宽些。如果有想从事会计方面的工作的，但是担心会计分比较高的，可以打哈财务管理这个擦边球（纯属个人建议）。")])])}),[],!1,null,null,null);_.default=s.exports}}]);