(window.webpackJsonp=window.webpackJsonp||[]).push([[39],{221:function(v,_,p){"use strict";p.r(_);var t=p(7),s=Object(t.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("h1",{attrs:{id:"商学院"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#商学院"}},[v._v("#")]),v._v(" 商学院")]),v._v(" "),_("blockquote",[_("p",[v._v("本文于2017年录入/高2014级17班佚名姓杨")])]),v._v(" "),_("hr"),v._v(" "),_("p",[_("strong",[v._v("学院简介：")])]),v._v(" "),_("p",[v._v("北京语言大学创办于1962年，当时校名为“外国留学生高等预备学校”，1965年1月9日经周恩来总理批准，定名为“北京语言学院”，后来又简化为“北京语言大学。北京语言大学可以说是为外国人开设的大学。")]),v._v(" "),_("p",[v._v("北语虽然在重庆不是非常出名招生比例也少，但是在北京高校知名度和热度还是蛮高的一个学院，尤其来北京工作学习的外国人，随便问一下肯定都知道这个学院。北京语言大学，在清华旁边，属于四五环内，外国人比例很高。")]),v._v(" "),_("p",[v._v("北语商学院算是北语比较年轻的学院，原来叫国际商学院，近几年改为了更简单的商学院，但这里我还是用了国际商学院这个旧称，因为比起别的学校北语具有特殊的国际化优势。")]),v._v(" "),_("p",[v._v("来到北语之后可以根据兴趣报一门小语种，也可以辅修日语、英语、法律、金融，但如果辅修的话会比较辛苦。")]),v._v(" "),_("p",[_("strong",[v._v("专业介绍：")])]),v._v(" "),_("p",[v._v("一、课程")]),v._v(" "),_("p",[v._v("北语商院商学院下设金融系、会计系、国际经济与贸易系、管理系。所有专业课程都是用双语讲授专业课，教学组织模式由中外学生分班授课向同堂上课。")]),v._v(" "),_("p",[v._v("由于学姐是金融系，就详细介绍一下金融系的课程吧。金融学大一开设的专业课程有微观经济学、微积分、会计学。大二开设的专业课程有国际金融学、货币银行学、管理学、宏观经济学、线性代数、概率论、财务管理。然后大三大四的课程都相对较少一些了。")]),v._v(" "),_("p",[v._v("经济类学科所开设的专业课程，会计学和国际金融都有很多计算题目，这方面有所顾忌的同学，怕是要努力了。此外背的东西也很多，尤其是货币银行学，宏观经济学、微观经济学中则需要概念上的理解，这两本经济学都是用的清华的教材，不过主要还是以老师讲的内容为主，考虑考研的同学可以另外再找曼昆版或其他版本自行阅读。")]),v._v(" "),_("p",[v._v("作为语言大学里面的专业，自然语言课程就多了很多。英语精读每周四节，然后其他的话，外教课，听说，读写，不必英语系的学生少。相当于学习了金融和英语两个专业。")]),v._v(" "),_("p",[v._v("周围不少同学也会选择辅修一门小语种，比较热门的肯定是日语、西班牙语、德语、法语之类，通常是周末和平时晚上上课。")]),v._v(" "),_("p",[v._v("二、考试")]),v._v(" "),_("p",[v._v("根据老师的不同会有半期或者小测，期末的话老师一般来说会在专业课考前会勾重点，但学姐也有遇到老师告诉你整本书都是重点的……那么平常上课就要注意了。数学像微积分、概率论、线性代数等纯数学的学科，大学老师可不会重复讲，也不会把习题一一讲解或者给答案，可以自己买一本习题辅导教材，如果决定要考研，自己早点看书刷题吧，把例题教材后面的题都做完弄懂。")]),v._v(" "),_("p",[v._v("至于英语考试的问题，统一考四六级和专四专八，英语四级在大二上学期考试，六级则是大三，在我们学校专四和专八是必考的，分别是在大二下学期和大四上学期。专业四级通过率在我校时百分之九十以上，但专业八级就要难一些，但是考出来写是很有用的。")]),v._v(" "),_("p",[v._v("三、就业")]),v._v(" "),_("p",[v._v("银行，证券，保险，审计，就业能去这些地方固然是最好的。但若是想要本科从北语的金融专业出来去银行是很难的，毕竟北京竞争激烈，压力也很大。作为北语的毕业生，和同专业的学生相比，优势就在于有语言背景。想要去外企一定把四六级的分数刷高一点，另外一定要多参与各种各样的实践性活动提高自己的执行、迅速适应的能力。同时，在工作中踏实努力也非常重要，有本科毕业的学长在层层面试中干掉了清华北大、北外研究生等大牛，成功入职，其制胜绝招就是一定要有自信。")]),v._v(" "),_("p",[v._v("北语越来越多优秀的学长学姐们在毕业后进入了四大会计师事务所实习,去四大实习不需要会计证书等，但建议先在学校期间先考一个会计初级证书，四大实习工资、待遇不错，但压力相当大，加班到半夜是常事，而工作之后很难再像在学校一样准备考试了。如果能毕业后能在四大会计师事务所工作几年，对未来发展很好的。")]),v._v(" "),_("p",[v._v("四、读研与出国")]),v._v(" "),_("p",[v._v("北语鼓励出国，但不鼓励考研，尤其是跨专业考研。当然也有很多金融系的学习考到了人大、对外经贸、北外、上外等。但要从北语这所非985非211外国语大学的非语言专业出来，去一所更好的大学读研，非常难，二度高考的精神是要有的。")]),v._v(" "),_("p",[v._v("出国方面，北语有很好的条件，学校和很多国外的大学有项目合作，澳大利亚西澳大利亚大学、澳大利亚麦考瑞大学、澳大利亚科廷大学、美国莫瑞州立大学、美国密西西比大学、英国伦敦欧洲商学院(英国摄政大学)、英国伯明翰大学、爱尔兰都柏林理工大学、芬兰于韦斯屈莱应用技术大学等校际交流协议，学院每年选派优秀学生去对方学校学习。")]),v._v(" "),_("p",[v._v("五、学生活动")]),v._v(" "),_("p",[v._v("商院活动丰富，体育、表演和各种比赛一直是非常厉害的。虽然北京语言大学是一所比较小的学校，但北语的学生很优秀，综合性人才较多，尤其是商学院的学生，非常努力，在各种活动中都表现得很好。整体而言，女生比男生优秀，比男生努力。")])])}),[],!1,null,null,null);_.default=s.exports}}]);