(window.webpackJsonp=window.webpackJsonp||[]).push([[410],{593:function(v,_,t){"use strict";t.r(_);var p=t(7),s=Object(p.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("h1",{attrs:{id:"武汉大学-概述"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#武汉大学-概述"}},[v._v("#")]),v._v(" 武汉大学 概述")]),v._v(" "),_("blockquote",[_("p",[v._v("文/李凯 蒋希 蒋燕眉")])]),v._v(" "),_("p",[v._v("聂晓松张潇予")]),v._v(" "),_("p",[v._v("本文来自《我眼中的大学（2012版）》")]),v._v(" "),_("p",[v._v("好早以前就看到有这么一个“告诉下一届南开人什么是大学”（是这么说的么？）当时真的有一种“啊！一定要告诉他们，不要让更多的南开人跟我一样志愿填的不明不白！”的想法，可是一直拖拖拉拉到今天才真正的开始写，大概这就是所谓的情到深处下笔难言吧。")]),v._v(" "),_("p",[v._v("学校综述")]),v._v(" "),_("p",[v._v("一、学校介绍")]),v._v(" "),_("p",[v._v("国立武汉大学，位于湖北省武汉市武昌区，珞珈山下，东湖之滨，据说是“中国最美的大学之一”。学校即将迎来120周年的校庆，保留的不少老建筑也在修葺中，比如老图书馆、珞珈山十八栋、老斋舍、宋卿体育馆等，这些建筑中西合璧，古朴典雅。当然也有很多奇怪的现代建筑。学校绿化也很好，树种很多，以英国梧桐、银杏和杉树最常见（其实还有几种很常见可是我不认识），每年春天3月中下旬，樱花开放，雪白的花朵与庄严肃穆的老斋舍就成为了国立珞珈山人民公园最亮丽的风景。")]),v._v(" "),_("p",[v._v("学校目前分为四个学部，分别是文理学部、工学部、信息学部和医学部。文理学部是武大的老校区，工学部是在2000年合并进来的与文理学部没有间隔，信息学部是曾经的武汉测绘大学与武大正门隔了一条交通极其混乱的街，医学部位于东湖的另一边很多人四年也不会去一次。")]),v._v(" "),_("p",[v._v("二、学部介绍")]),v._v(" "),_("p",[v._v("对于文理学部来说，优势还是传统的文史哲一类的；武大的法学院也是很不错的；武大的法语也很好因为武汉与法国的交流很密切（不知道是不是可以说毕竟是曾经的法租界）；听我一个武汉本地的朋友说武大的生科也是不错的。生物工程是武大的一块牌子，重医的学生就有大一在武大生科委培的；还有呢就是现在很火的经管院这个一会详细说。")]),v._v(" "),_("p",[v._v("那么信息学部呢，位于武汉大学本部对面（中间隔街一条），环境和本部优美的景色比起来小有差距，但是寝室住宿条件比本部好一些，男女比例大约是2：1。信息学部里面有五院一系，分别为遥感学院，测绘学院，国际软件学院，资源与环境学院，电子信息科学学院以及印刷与包装系。信息学部整体的学习氛围较为浓厚，平时的文娱活动也挺多。而且信息学部不大不小，感觉正好适合生活以及学习，寝室、教学楼、食堂都隔得挺近，但又不是单纯的三点一线。信息学部的牛逼专业是遥感与测绘，测绘很强大说是亚洲数一数二的，有数位两院院士，但是比较辛苦，在武大校内经常会看到有测绘的同学带着专用仪器实地测量。遥感专业稍后会有详细介绍。")]),v._v(" "),_("p",[v._v("工学部和医学部对于整个武大而言就比较鸡肋了，貌似武大的口腔医学还不错。")]),v._v(" "),_("p",[v._v("三、校内生活")]),v._v(" "),_("p",[v._v("武大文理学部的寝室分为樱园、枫园、梅园、桂园、湖滨和三环，每个地方都有与名称相对应的植物，而寝室内部最差的就是枫园了，老房子又小条件又差。不过最近重庆崽儿李晓红当校长了以后开始大面积的装空调，可能等下学期开学了寝室条件会有好转。食堂的话，虽然数量很多但是质量不行。教学区，上学期新图书馆开始投入使用了一共有五层藏书还是可以，就是要到期末的时候自习区不够用，新图还有一些奇怪的地方没去过不好说；对教学楼，不做评论。武大的双学位要求的绩点是2.7也就是说你的均分要在75以上，而你如果想选择一些比较热门的学位，是会考虑你的绩点在整个报名人员中的排位，比如金融就只收报名的前200名。同时，你也可以选择在武汉的其他6所高校修双学位，比如华中科技大学、中南财经政法大学。武大的学习氛围，不是特别浓厚。武大的活动氛围倒是很浓，各种活动都有而且不会停下来。武大的辩论队很有名，多次获得国际大学生辩论比赛的冠军。如果你很喜欢社团活动或者对辩论很有兴趣，武大会是一个不错的选择。")])])}),[],!1,null,null,null);_.default=s.exports}}]);