(window.webpackJsonp=window.webpackJsonp||[]).push([[417],{600:function(v,_,p){"use strict";p.r(_);var t=p(7),o=Object(t.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("h1",{attrs:{id:"西交大机械工程个人感想"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#西交大机械工程个人感想"}},[v._v("#")]),v._v(" 西交大机械工程个人感想")]),v._v(" "),_("blockquote",[_("p",[v._v("本文于2017年录入 练训豪")])]),v._v(" "),_("p",[v._v("专业介绍")]),v._v(" "),_("p",[v._v("主要课程：")]),v._v(" "),_("p",[v._v("基础课程：大学英语，大学物理，高等数，线性代数，大学计算机基础（VB、C不定），工程制图，大学化学等")]),v._v(" "),_("p",[v._v("专业课程：电子电工技术，机械设计基础，流体力学，机械精度设计，机械控制理论，材料成型技术，测试技术，建模仿真等")]),v._v(" "),_("p",[v._v("个人感受：大一课相对较少，比较轻松；大二课程重，实验较多；大三主要学习专业课程，偏理论，有些枯燥，实验多，实验主要涉及电子电路，机床操作，液压回路，建模仿真等。")]),v._v(" "),_("p",[v._v("主要学习c++,aotocad ,inventor,solidwork,matlab等等软件。理论偏枯燥，难度很大，如果对机械行业不敢兴趣的谨慎填报志愿。所学内容不太涉及机器人，主要涵盖各种机床，汽车零部件等。")]),v._v(" "),_("p",[v._v("未来就业输出主要方向：汽车，机床厂，航天用发动机，少量去手机厂（如华为）。")]),v._v(" "),_("p",[v._v("参观过比亚迪，航天四院，鼓风机厂，商用车厂等等，毕业后大致从事模具设计，生产线机器操作，零件研发等等内容。工作环境比起白领肯定差很多，机器轰鸣，油污等等免不了。")]),v._v(" "),_("p",[v._v("就业率高，不愁找不到工作，很多企业都有需求。西交17年本科毕业一般城市薪资差不多都是5k起。")]),v._v(" "),_("p",[v._v("生活环境")]),v._v(" "),_("p",[v._v("吃：学校食堂味道一般，米饭不怎么好吃，面食还不错。少油少辣，不放花椒，差不多就只放盐和一些奇怪的酱料。食堂内有西餐厅，偏贵。学校周围小饭馆较多，味道还行。西安川菜馆较多，味道比起重庆差一些。")]),v._v(" "),_("p",[v._v("住：独卫，公共澡堂定时开放，宿舍内有空调电扇，可使用中小功率电器（偶尔抽查宿舍，不过管得很松）。四人间，每层楼有直饮热水机。带阳台，可晾晒衣物。")]),v._v(" "),_("p",[v._v("在外面住房合租一个月600左右，房源很多。")]),v._v(" "),_("p",[v._v("购物方便，学校内有三个大超市，学校周围也有商场。")]),v._v(" "),_("p",[v._v("各个书院内有健身房，学校内也有体育馆，游泳馆。")]),v._v(" "),_("p",[v._v("玩：大雁塔等古迹，城墙可以骑自行车，海洋馆，动物园植物园，有年票套票出售。")]),v._v(" "),_("p",[v._v("交通还算方便，一天可以来回。除了古迹，学校附近有桌游吧，学校内有狼人杀之类的社团或者群，每周组织聚会。有KTV，电影院。游乐场，密室这些离学校就远了。")]),v._v(" "),_("p",[v._v("多数专业课程较重，出去玩的机会也不多。")]),v._v(" "),_("p",[v._v("社团：学校内优秀社团很多，活动丰富，让你学习组织活动，社团沟通，设计项目，拉赞助等等。社团氛围很好，容易相处，可能有大牛的分享会之类的。收获颇丰。")]),v._v(" "),_("p",[v._v("学校代表性活动：科技节，百团大战（社团展示），交大之星（才艺竞选），知识竞赛，马拉松等等。丰富有趣，舞台效果也挺好。   学校常年有讲座，涉及科技、历史、文化、技术等等各个方面，主讲老师往往是该方向权威，内容不枯燥，很有意义。")]),v._v(" "),_("p",[v._v("学习氛围")]),v._v(" "),_("p",[v._v("学习氛围较浓厚，图书馆借阅图书也方便，有WIFI。书籍种类多，能满足学习需求。自习室一到考试周就经常满座，有英语演讲等等社团帮助学习英语，也有各类关于计算机软件(如word exel )应用的学习讲座。部分专业奖学金获得较难，竞争较大。")]),v._v(" "),_("p",[v._v("最后总结：")]),v._v(" "),_("p",[v._v("西交以理工科为主，男多女少，谈恋爱有点难度，较大比例毕业后仍单身。教学质量在较前的行列，设施齐全。学校知名度在北方很受认可，北上广稍弱，不过最近几年有加大宣传。工作不用愁，很多著名企业都来校招。西交的宿舍环境比起很多学校都算不错了，没有热水器洗澡不方便，不过北方的学校大多数都是公共澡堂。吃这方面，南方人初来不习惯是正常的，食堂味道还行，但菜样式常年不变，久了就厌烦了。所幸学校周围小餐馆多，价格也在能接收范围内。课程较重，部分需要打卡，不打卡的课翘课什么的问题不是很大，考试难度中等，用点心复习就能及格。")]),v._v(" "),_("p",[v._v("本文作者是13级机械工程毕业生")])])}),[],!1,null,null,null);_.default=o.exports}}]);