(window.webpackJsonp=window.webpackJsonp||[]).push([[427],{610:function(v,_,p){"use strict";p.r(_);var t=p(7),s=Object(t.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("h1",{attrs:{id:"学校综述"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#学校综述"}},[v._v("#")]),v._v(" 学校综述")]),v._v(" "),_("blockquote",[_("p",[v._v("本文于2012年录入")])]),v._v(" "),_("p",[v._v("【前言】首先感谢为了这份资料辛苦的各位，刚刚忙完实践策划的大师兄刘力夫，第二天参加比赛但是还要熬夜重新写一遍的李爽，一堆作业没赶完但是在我的各种发飙之下挤出时间写的李怡然，紧张准备转专业考试的同时仍然写得很好的吴文静，刚刚参加换届选举马上跑回来赶稿的卿爽。")]),v._v(" "),_("p",[v._v("【我们的一点想法】关于这个活动，我很佩服发起它的同学们，大家怀着共同的南开情怀和对下一届的期待，愿意以自己的付出换来更多美好的结果。我原本是告诉写的同学们以客观的态度去写，但是后面觉得那样还是很官方。我们更愿意用略主观的口吻，表达我们的真实感受。正如大师兄所说，我们不是去教下一届在志愿上应该怎样填报，而是提供我们的意见，让他们对厦大有更多的了解。")]),v._v(" "),_("p",[v._v("【最后】在联系同学的过程中发生了很多，期间还有实践策划校选汇报一系列杂七杂八的事情。我在想，在写的同时是不是也是对我们自己的一种回顾和审视。大家都很忙，做不完的作业复习不完的功课，可是没有人抱怨自己有多累，还是一个字一个字地码了出来。我们共同的南开情怀，就就是支撑我们一直做下来的动力。")]),v._v(" "),_("p",[v._v("感谢每一位为此付出汗水和时间的同学：")]),v._v(" "),_("p",[v._v("刘力夫（10级10班/金融）")]),v._v(" "),_("p",[v._v("李爽（11级20班/王亚南研究院国际班）")]),v._v(" "),_("p",[v._v("李怡然（11级24班/会计系3班国会）")]),v._v(" "),_("p",[v._v("吴文静（11级22班/物理）")]),v._v(" "),_("p",[v._v("卿爽（11级24班/中文）")]),v._v(" "),_("p",[v._v("邬文静（11级24班/财政）")]),v._v(" "),_("p",[v._v("特别感谢李爽的辛苦整理，让每一篇分散的资料更有逻辑性。")]),v._v(" "),_("p",[v._v("【建校历史】")]),v._v(" "),_("p",[v._v("厦门大学属于全国重点大学，由爱国华侨陈嘉庚先生创办，地处经济特区厦门，有“南方之强”称号。在整个南方地区来看，厦大的名气比较大，学术氛围浓厚。")]),v._v(" "),_("p",[v._v("【地理位置】")]),v._v(" "),_("p",[v._v("厦大本部位于厦门市思明区，本部周围有海韵校区，另外有漳州校区（位于福建漳州市龙海市，目前是厦大2011级以及2010级学生和嘉庚学院学生使用。2012年将全体搬迁回厦门）和翔安校区（在建中）。")]),v._v(" "),_("p",[v._v("【主要优势学科】")]),v._v(" "),_("p",[v._v("经济学科、管理学科、化学、新闻传播学、历史学、法学、外交学（主要为东南亚及南洋关系研究）等。因为本人对其他学科缺少研究，这些院暂时也没有找到南开的学长学姐，所以难以评价。今后填报志愿时候，学弟学妹们应仔细查阅学科信息，详细解国家级重点学科建立时间、设立硕士、博士培养时间等。")]),v._v(" "),_("p",[v._v("【生活环境】")]),v._v(" "),_("p",[v._v("校园环境优美（详情可参见芒果台雷阵雨一剧或百度之），与武大并称为“中国最美校园”，尤其是本部已被全国各地游客攻占（所以我这是吐槽么==），本部外有白城海滩、对门望得到鼓浪屿。(厦门其实也没有什么很好玩的地方啊，全国各地的亲们！！！一到了节假日，人多得你想死啊！)厦门空气湿润，一年四季都有花开，自然条件非常好，不像北方到了冬天只能看缠着假叶子的树==。")]),v._v(" "),_("p",[v._v("学校的生活质量比较高，厦门这边的环境确实很适合居住，厦大的宿舍条件在全国范围内也是相当好的，有空调~（当然今年本部一些宿舍改造成六人间和八人间过后质量会有所下降）。学校的饮食虽然说不能完全习惯但是还是不错，学校内外也有许多咖啡馆等等供休闲的地方，整体上来说，除去学习和一些校内活动的紧张繁忙外，厦大的整体环境很适宜生活和潜心学术，有度假村的感觉。此外，厦大水免费米饭、矿泉水不要钱。食堂也比较便宜，味道也还不错。寝室基本都是四人间，只有少量男生宿舍是六人间（但是六人间的老楼非常漂亮！）。")]),v._v(" "),_("p",[v._v("【特别说明】")]),v._v(" "),_("p",[v._v("2012年9月后，漳州校区学生即将全部搬回本部、海韵校区、翔安校区。海韵校区距离本部较近（自行车10min左右），翔安校区已经到了岛外（据5月份到翔安参观的同学讲，真的很荒，教学楼和住宿楼仍在建……所以，2012届是去拓荒吧==）就目前了解到的情况，2012年下半年各院（此处讨论的是本科）去向的安排是：")]),v._v(" "),_("p",[v._v("本部：材料学院法学院公共事务学院管理学院化学化工学院建筑与土木工程学院国际关系学院人文学院外文学院物理与机电工程学院新闻传播学院艺术学院经济学院（10级和09级）")]),v._v(" "),_("p",[v._v("翔安：公共卫生学院国际学院海洋与地球学院环境与生态学院生命科学学院药学院医学院")]),v._v(" "),_("p",[v._v("海韵：经济学院（11级和12级）软件学院数学科学学院信息科学与技术学院。")]),v._v(" "),_("p",[v._v("所以，各位在填报志愿的时候，要对三个校区有所了解，在填报与一些可能会迁至翔安校区的专业上，慎重考虑。")]),v._v(" "),_("p",[v._v("【其他特点】")]),v._v(" "),_("p",[v._v("厦大三个学期的设置是一大奇葩之处，每学年有三个学期，一般是9月至次年1月为第一学期，2月至6月为第二学期，紧接着就是坑爹的小学期了，时长五个星期左右。时间跨度大，学生选修的课程少，“水”的课程不在少数，能够学到的东西十分少。大多数学生在学期除了留在寝室、泡馆之外就只有旅游或者组织社会实践了。不过小学期也是许多暑期实践的同学可以利用的时间段，也有不少人选择去厦门学托福或者其他语言。总的来说，时间的利用还是在于自己吧。")]),v._v(" "),_("p",[v._v("【双学位简言】")]),v._v(" "),_("p",[v._v("厦大的双学位是比较容易修的。相比于人大要求高考分数必须高于辅修专业的分数这种变态规定，厦大辅修的标准只要不挂科就可以了。这种开发的态度我比较欣赏。")]),v._v(" "),_("p",[v._v("开设辅修的专业有很多。就文科来说，经济、新闻、广告、法学都可以修。不过修起来比较辛苦，要牺牲掉周末，很多人都没能坚持到最后。")]),v._v(" "),_("p",[v._v("但是老师都是各个学院里的正常任课的老师，还是能学到很多东西。")]),v._v(" "),_("p",[v._v("【对外交流】")]),v._v(" "),_("p",[v._v("有去台湾、新加坡等东南亚地区念书的计划的同学可以考虑厦门大学。因为地理位置上比较靠近，厦大在这些地区的交流合作比较多。掌握的留学资源也比较多。值得说的是，厦大与台湾大学的交流项目，学校有相当一部分的港澳台学生，基本上每年都可以申请去台湾大学交流，从开阔眼界加深交流来说还是不错")])])}),[],!1,null,null,null);_.default=s.exports}}]);