(window.webpackJsonp=window.webpackJsonp||[]).push([[57],{239:function(v,_,t){"use strict";t.r(_);var p=t(7),r=Object(p.a)({},(function(){var v=this,_=v._self._c;return _("ContentSlotsDistributor",{attrs:{"slot-key":v.$parent.slotKey}},[_("h1",{attrs:{id:"法学院"}},[_("a",{staticClass:"header-anchor",attrs:{href:"#法学院"}},[v._v("#")]),v._v(" 法学院")]),v._v(" "),_("blockquote",[_("p",[v._v("本文于2017年录入/北京师范大学法学院官方微信平台")])]),v._v(" "),_("p",[_("strong",[v._v("学院概述：")])]),v._v(" "),_("p",[v._v("北京师范大学法学学科始于1995年哲学系设立的法学专业，2006年成立法学院。国际刑法学协会中国分会常务副主席、中国刑法学研究会副会长、中国犯罪学研究会副会长卢建平教授担任法学院院长。著名民法学家江平担任法学院名誉院长。")]),v._v(" "),_("p",[v._v("经过10年的不懈努力，北师大法学学科已跻身于全国A+类法学学科行列，并进入世界顶级法学学科QS排名前200，居中国内地高校第8名，拥有法学一级学科硕士学位授予权和法律硕士学位授予权，拥有刑法学、诉讼法学、民商法学等三个二级学科博士授予权，并拥有刑法学和诉讼法学方向的博士后招收资格，实现了学科的跨越式发展。2012年首批教育部入选卓越法律人才教育培养计划，并获批应用型复合型法律职业人才教育培养基地和涉外法律人才教育培养基地。")]),v._v(" "),_("p",[v._v("学院建设了一支高水平的教师队伍。教师已由最初筹建时的16人，发展到63人。其中，全国“十大杰出青年法学家”2人；“新世纪优秀人才支持计划”3人；荣获“北京市教学名师”称号1人；担任国际性学术组织领导职务的4人；担任全国专业学会常务理事以上职务的教师8人。学院还聘请了来自全国人大常委会、最高人民法院、最高人民检察院等实务部门以及剑桥大学、牛津大学、纽约大学、慕尼黑大学、北京大学、中国社科院等国内外一流大学和学术机构的180多名知名专家学者担任特聘教授和兼职教授。")]),v._v(" "),_("p",[v._v("法学院依托北京师范大学这一享誉海内外的著名学府，将继续坚持高起点、研究型、国际化的发展特色，着力培养一流的法律实务人才和法学高端研究人才，致力于把学院建设成为国内一流、国际知名的法学教学科研机构。")]),v._v(" "),_("hr"),v._v(" "),_("p",[_("strong",[v._v("科学研究：")])]),v._v(" "),_("p",[v._v("学院科研实力雄厚，成果丰硕。近三年来在CSSCI来源期刊/集刊上发表学术论文超过400余篇，出版学术专著100余部；承担国家自然科学基金和国家社会科学基金项目20余项，承担教育部、司法部等省部级项目100余项；获得省部级以上奖励20余项。")]),v._v(" "),_("hr"),v._v(" "),_("p",[_("strong",[v._v("人才培养：")])]),v._v(" "),_("p",[v._v("法学院致力于培养具备深厚人文素养和现代法治理念、具备娴熟法律技能、富有创新能力和国际视野的复合型法律人才。学院现有刑法学课程被评为国家级精品课程，刑事诉讼法课程被评为北京市级精品课程；刑法学科教学团队2010年荣获“国家级教学团队”称号；学院高度重视学生实践能力培养，与法院、检察院、律师事务所等实务机构合作，建立了30多个专业实践基地。其中依托教育部“法学教育实践基地”建设项目，与北京市人民检察院、河南省高级人民法院、北京市海淀区人民检察院合作共建有3个国家级法学教育实践基地，为学生从事实务训练提供了便利条件。鼓励学生积极参与各种科研项目，锻炼和提高科研能力，其中超过半数本科生有机会获得国家级大学生创新创业训练计划、北京市大学生科学研究与创新创业行动计划和学校本科生科研基金等项目资助。法学院与四十余个境外法学院签订了学术交流协议，积极鼓励教师、学生赴境外交流，同时多次聘请外籍教授为本科生授课。")]),v._v(" "),_("p",[v._v("·专业介绍：")]),v._v(" "),_("p",[v._v("法学专业（卓越实验班）学院依托教育部应用型复合型法律职业人才教育培养基地设立卓越实验班，致力于培养具备深厚人文素养、坚定法律信仰、扎实法学功底、娴熟法律技能和开阔国际视野的应用型、复合型卓越法律人才。卓越实验班创新课程体系，加大了法律实务课程和学科交叉课程的比重，并实行导师制，为每位本科生安排导师向学生提供学业和生活指导。法学院与国外多所高水平大学合作，通过互派留学、暑期学校等方式拓展学生的国际视野。与此同时，以专业实践基地为载体，与法律实务部门联合培养适应社会需求的应用型、复合型法律人才。")]),v._v(" "),_("p",[v._v("法学专业（瀚德实验班）自2013年起，在钟瀚德基金会支持下设立瀚德实验班，旨在培养国际化的高端法律人才。主要举措：瀚德实验班采用国内、国外合作，学校、实务部门协同的全新培养模式，构建科学合理的全新课程体系，强化学生的法律专业能力和双外语应用能力培养。学生除学习法律专业课程和英语课程之外，还将学习第二外语（德语或葡萄牙语）。学院聘请外籍教授开设多门全外文教学的专业课程；在读期间，学院资助60%左右的优秀学生赴所学第二外语国家高水平大学留学一年；学生修满规定学分后，获得法学学士学位和北京师范大学双学士学位证书。")]),v._v(" "),_("p",[v._v("·主要课程")]),v._v(" "),_("p",[v._v("法理学、中国法制史、宪法学、刑法学、民法学、刑事诉讼法、民事诉讼法、行政法与行政诉讼法、经济法学、商法总论、知识产权法、国际公法、国际私法、国际经济法、公司法、金融法、环境保护法、劳动与社会保障法、模拟法庭实验、法律诊所等。")]),v._v(" "),_("p",[v._v("·合作交流")]),v._v(" "),_("p",[v._v("法学院始终坚持国际化发展战略，先后与美、德、法、英、意、西、葡、加等国的40多个高校、机构签订了协议。其中与美国埃默里大学、乔治城大学、宾夕法尼亚州立大学、英国阿伯丁大学、法国巴黎二大、图卢兹大学、意大利路易斯大学、西班牙巴塞罗那大学、葡萄牙科英布拉大学等国外大学合作开展有交换生项目和暑期项目，每年组织百余名本科生赴国外学习。近些年选派教师、学生出国交流和接待国外学者来访超过500人次；承办“当代刑法国际论坛”、“两岸四地刑事法论坛”等国际、区际学术会议16次。")]),v._v(" "),_("hr"),v._v(" "),_("p",[_("strong",[v._v("学生活动：")])]),v._v(" "),_("p",[v._v("法学院成立有学生会、法学社、普法志愿者协会、中美法学比较研究会等学生社团，每年都组织模拟法庭大赛、模拟仲裁大赛、辩论赛、法律文化节、普法宣传等富有法学专业特色的学生活动，服务于学生成长成才。法学社主办的首都八校联合辩论赛，在首都法律院校中有广泛的影响力。北京师范大学法学院代表队曾在国际上享有盛誉的“国际人道法”模拟法庭竞赛第二届中国区选拔赛上获得第三名，在第一、二届首都高校模拟仲裁大赛中连续两届获得冠军，在第五、六、七届北京市大学生模拟法庭竞赛中连续三次获得一等奖。在第五十八届杰赛普（JESSUP）国际模拟法庭竞赛暨第十五届中国大陆赛区选拔赛上，北京师范大学法学院代表队获得一等奖。")]),v._v(" "),_("hr"),v._v(" "),_("p",[_("strong",[v._v("就业情况:")])]),v._v(" "),_("p",[v._v("近年来，约半数应届本科毕业生保送或考取本校、清华大学、北京大学、复旦大学、中国政法大学、浙江大学等国内名校硕士研究生；或到美国乔治华盛顿大学、卡内基梅隆大学、西北大学、德国法兰克福大学、英国伦敦国王大学、伦敦大学学院、法国巴黎政治大学、西班牙巴塞罗那大学、卡洛斯三世大学等国外名校留学深造。其余主要到各级人大、法院、检察院等政府机关和律师事务所、仲裁委员会、银行、新闻媒体、企业法务部门等法律服务机构或企事业单位从事法律实务工作。")])])}),[],!1,null,null,null);_.default=r.exports}}]);