(window.webpackJsonp=window.webpackJsonp||[]).push([[90],{272:function(n,t,a){"use strict";a.r(t);var e=a(7),i=Object(e.a)({},(function(){var n=this,t=n._self._c;return t("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[t("h1",{attrs:{id:"商学院"}},[t("a",{staticClass:"header-anchor",attrs:{href:"#商学院"}},[n._v("#")]),n._v(" 商学院")]),n._v(" "),t("blockquote",[t("p",[n._v("本文于2014年录入/高2013级21班陈虹宇")])]),n._v(" "),t("p",[n._v("学校：香港城市大学学院：商学院")]),n._v(" "),t("p",[n._v("学习氛围：香港作为一个亚洲乃至世界性的国际金融中心，商业相当完善，自然在这里学习商科是个不错的选择。据说CityU的商学院在亚洲排名前三（学姐其实也不能保障数据的真实性TAT，算是自卖自夸吧），商院的professor大都是有相当的工作经历和国外留学的经历，因此教学也会比较贴近实际，实用性很强，讲解也都很生动，让你觉得每个老师都很有魅力，上课不会觉得枯燥啦！并且因为老师的国际背景的缘故，课程设置和教学内容当然也是跟得上国际潮流的，除了国内常有的课程，更有informationmanagement,globalbusinesssystemmanagement这之类的高大上课程~学校除了本地和大陆的师生，另外还有不少其他国家的老师和学生，比如马来西亚，韩国，英国等等。而很多香港本地学生的商业创新意识和能力都很强，和他们一起做project会学到很多。至于内地生，商院自古多学霸，很多都是本省填报城大的最高分录取者，而商院在北京和上海的录取分直逼北大清华，所以和学霸们在一起，学习还是很有动力的~说得学术一点，就是和一群优秀的人在一起，会让你变得更优秀。（捂脸跑走~~~）")]),n._v(" "),t("p",[n._v("活动氛围：嗯嗯。香港的学生都比较活跃，因此活动挺多的，然后商院也有自己的学生会（这边称之为庄），这边的学生一般上庄之后都会把庄物当成事业一样经营，竞选的时候几天通宵也是常事，平常也是相当耗费时间和精力的，所以根据自己的情况适当选择吧。当然，内地生的庄CSSAUG下辖的社团也有很多活动，比如嘉年华，话剧节之类。热爱体育的孩子们，每年香港的八大校都会有内地生足球，篮球联赛，想在本校和外校的帅哥和美眉们面前一展风姿的孩子有机会啦！至于为何不是以院系的名义参加，这个，城大一般都是内地生")]),n._v(" "),t("p",[n._v("和local学生各自活动，然后每个院的内地生本来就不多，所以。。。。")]),n._v(" "),t("p",[n._v("所学课程：第一年是商院学生的通识课，每年的上法不一样，反正我们今年上的是Economics1,management,quantitativeanalysisofmanagement,accounting,marketingandinformationmanagement。第一年开学要参加一个鉴定考试，然后根据成绩分配去上不通过的英语课程。除了大家都会上的EL或者UE以外，商院学生还要上Englishforbusiness。之后还有众多的选修课（getawayeducation），语言课。值得一提的是很多选修课相当不错，比如学姐今年上的茶道课，就是由日本茶道大宗里千家在香港的分支的teamaster作为监督~~O(∩_∩)O")]),n._v(" "),t("p",[n._v("专业介绍：大家这届应该是第三学期才会选专业。根据GPA和自己的兴趣选专业，大概有accounting,finance,informationmanagementsystem,marketing,management六个大department的十五个不同专业。具体的专业介绍会在选专业之前由各个department开设的briefing时讲解，总之各个专业都有自己的优势，看自己的兴趣啦。一般来说城大比较好的专业是accounting还有marketing,内地生一般多选quantitativefinance(计量金融)。具体的专业介绍我也无能为力啦，因为我们现在也没有选定专业(＞﹏＜)")]),n._v(" "),t("p",[n._v("笔者感言：来到香港不知不觉也有一年多了，居然也到了自称学姐的时候了。记得当初填报志愿的时候亦是忐忑，因为之前并没有南开在商院的学长学姐作指引o(╯□╯)o，就这样稀里糊涂地填了一个在考前才知道的学校（后来发现很多小伙伴都是这样的╮(╯╯╯)╯）。记得一个港大的学姐说过，如果想要学习金融之类需要扎实专业基础和数学功底的专业，又想之后出国深造，那么不如就在北清人复；如果想学习informationmanagement之类的国际新兴专业,marketing或者management之类的实践性很强的专业，那么不要犹豫地来香港吧！")]),n._v(" "),t("p",[n._v("本文于2015年录入/高2014级10班刘晋妤")]),n._v(" "),t("p",[n._v("学校：香港城市大学学院：商学院")]),n._v(" "),t("p",[n._v("学习氛围:香港作为国际金融中心，商业及制度都比较发达，在香港学习商科自然很不错，有更多机会去接触，了解，应用，实习。香港的学校课程设置与内地大有不同，我们没有班级，班主任，只以学院，学系，年级区分，没有固定的同班同学。商学院的上课形式一般是lecture（professor授课）+tutorial(导师针对教授授课内容引导小组讨论或复习)。Professor大多都有相当的工作经历和出国留学经历。而课上常常会遇到来自不同国家比如韩国，马来西亚，美国，英国的老师及学生。城大学习任务也是多样化，通常有presentation,groupproject,individualreport，classesparticipation,mid-term,final,quiz.这些都是衡量学生成绩的指标，因此不仅会考察学生书本上知识掌握情况，还有对演讲，合作学习等不同能力的考察。商学院相比科学及工程学院课业较重，presentation及小组做project的机会较多。虽然课不多，但每堂课下来workload比较重。比起内地，我们上课时间少，自由时间较多，需要对自己时间有个较好的规划。大一都还没分专业，上的差不多算是通识课，我们这届商院year1上的是:Theintroductiontoaccounting,microeconomics,marketing,management,informationmanagement,operationmanagement.大一还算轻松，不过听说到了大二商院就会忙得飞起。")]),n._v(" "),t("p",[n._v("活动氛围：香港的学生参加活动的积极性都很高，活动种类也是挺多的，有不同的hall举办的，有学校不同部门，不同学院举办的，有相关本地学生办的，当然也有内地生办的。这边我们习惯把社团称作庄。这边的学生一般上庄之后都会把庄物当成事业一样经营，竞选的时候几天通宵也是常事，平常也是相当耗费时间和精力的，所以根据自己的情况适当选择吧。内地生的庄CSSAUG下辖也有很多社团，比如篮球队，足球队，羽毛球社，乒乓球社，舞蹈社，器乐社，动漫社，话剧社等等，平时也会举办非常多的活动:OCAMP,嘉年华，体育嘉年华，K歌大赛，中国文化节，约？约！，百佳送货，二手书寄卖，夏季储物等等。这些都是锻炼领导，组织，沟通能力，增强自信，拓宽人际网络的好机会。关于商院的，校内校外都有很多商赛，都是学以致用，拓宽眼界的平台。听学长学姐们说，到了大二分了专业过后，几乎每一个商院的学生都会去参加哟~。")]),n._v(" "),t("p",[n._v("专业介绍：大家这届应该是第三学期才会选专业。根据GPA和自己的兴趣选专业，学校要求是平均GPA在top20%里的学生能自己选，而在后面80%的如果想学自己感兴趣的专业得通过相关的面试。不过不用担心哒，内地生的竞争力还是很强的。商院大概有accounting,finance,informationmanagementsystem,marketing,management六个大department的十五个不同专业。具体的专业介绍会在选专业之前由各个department开设的briefing时讲解，总之各个专业都有自己的优势，看自己的兴趣啦。一般来说城大比较好的专业是accounting还有marketing,内地生一般多选quantitativefinance(计量金融)。具体的专业介绍我也无能为力啦，因为我们现在也没有选定专业(＞﹏＜)")]),n._v(" "),t("p",[n._v("笔者感想：来香港都一年了，自己也从小鲜肉成长为学姐。最初由于香港教学和内地的种种不同还不太适应，大一比较轻松所以浪得也比较厉害，也算是释放了应试教育的压抑，过渡到对自主学习能力要求更高的大学生活吧。在这儿接触不同国家地区不同文化习俗的人，拓宽眼界增长见识，与以前想象中的大学生活有些不同，但也算丰富充实。在香港学商科，机会很多，平台很宽，踏踏实实学好专业知识，好好利用这些资源，想到这些自己对未来也充满了信心。当然，对南开的学弟学妹们来说，在这片沃土上大展才华更不在话下！")])])}),[],!1,null,null,null);t.default=i.exports}}]);