(window.webpackJsonp=window.webpackJsonp||[]).push([[93],{275:function(a,n,i){"use strict";i.r(n);var e=i(7),t=Object(e.a)({},(function(){var a=this,n=a._self._c;return n("ContentSlotsDistributor",{attrs:{"slot-key":a.$parent.slotKey}},[n("h1",{attrs:{id:"人文社会科学院"}},[n("a",{staticClass:"header-anchor",attrs:{href:"#人文社会科学院"}},[a._v("#")]),a._v(" 人文社会科学院")]),a._v(" "),n("blockquote",[n("p",[a._v("本文于2014年录入/高2013级8班张诗皎")])]),a._v(" "),n("p",[a._v("学校：香港城市大学学院：人文社会科学院")]),a._v(" "),n("p",[a._v("学习氛围：繁华港城钟神秀，人文城大谱春秋。论人院人才结构，local较多，质量（就学霸程度而言）良莠不齐，内地生少，puremale更是凤毛麟角，GPA普遍偏高，各国友人云集于此，文化交汇火花四溅。论人院学业特色，课程、考试少，paper、reading多，有时需要特殊的present（课堂展示）技巧，有大把时光由你自由支配。香港文化整体偏重实用性，文学气息薄似秋云，对亲爱的中文，你多半只能轻挥衣袖，说声拜拜。但来到交通便捷、活泼开放、挥金如土的城大，你能够收获广阔的视野、深刻的情怀，各领域优秀的professor都为你")]),a._v(" "),n("p",[a._v("敞开生动丰富的课堂，更有不少去国外交换、深造的机会。")]),a._v(" "),n("p",[a._v("活动氛围：城大的社团不胜枚举，尤其舞蹈社多胜繁星，但多为local的组织，这里称之为“庄”。各庄为招募或宣传尽展奇葩之能事，在校园中总能见到造型诡谲、肆虐疾呼之团体，这是一种传统神圣的仪式，名为“喊庄”，对喊者与被喊者都是一场生命的考验。地生的大小活动团体则隶属CSSAUG掌管，话剧、音乐、动漫等等各展风姿，总有一款适合你。在活动中崭露头角，于社团里谱写青春必然是大学不可或缺的组成部分，但劳心费力同样无法避免，对于有的庄员，忙碌时通宵不眠也是家常便饭。")]),a._v(" "),n("p",[a._v("所学课程：第一学年本院共开设六门专业课，主要介绍不同领域的入门知识，内容包罗万象（而虚空），分为两大类，一类偏文学，包括re-imagingEnglish,manyfacesoflanguage,mediaandcommunication；一类偏社会科学，包括Asiaandtheworld,discoveryofgovernment,individualsandsociety。前者主要培养creativity，有机会接触到诗歌、戏剧、小说等文学作品，部分作业比较有创造性，如拍视频、制作facebook专页等；后者看重criticalthinking，课程中不时涉及自由（激进）民主（反共）之内容，目的在于提高同学们对政党、国际关系及一些社会问题的理性认识。每个学期需从以上两个类别中各挑一门修读，共修四门。不必为选了或没")]),a._v(" "),n("p",[a._v("选的遗憾，本质都差不多（坑），且上且珍惜。除专业课外，第一学年共需选修三门GE，即选修课，分为三个area：人文类、商学类、科学类，涵盖天文地理、宇宙洪荒、文化娱乐、士农工商，且中（粤语）英文皆具，可谓气象万千，大学四年共需选修七门，每个area至少选一门。各GE课从难度到给分都有较大差异，要选到好的GE需经历一番恶战（月圆之夜，抢课之时自见分晓），前辈经验固然值得借鉴，运气和勇气也十分关键。")]),a._v(" "),n("p",[a._v("专业介绍：理论上专业将在第一学年末进行选择，可供选择的专业有：MediaandCommunication，DigitalTelevisionandBroadcasting，TranslationandInterpretation，Chinese，Linguistics")]),a._v(" "),n("p",[a._v("andLanguageTechnology，SocialWork，Psychology，AsianandInternationalStudies，AppliedSociology，PublicPolicy,Management,andPolitics，EnglishStudiesEnvironmentalPolicyCultureandHeritageManagement，Criminology。丰富如此，应该足以满足各位同学的不同需求。选专业主要依据个人兴趣，GPA高者有优先选择权。各个专业具体如何，我目前也不甚了然，就不误导下一届了。")]),a._v(" "),n("p",[a._v("笔者感言：混入港漂圈将近一年，好似日夜碌碌，又好似年华轻浮。生活总在没准备好和没觉察到的时候发生着变化，对香港的印象也与来之前大相径庭。文明、开放、时尚、包容、")]),a._v(" "),n("p",[a._v("实际、秩序井然，这座城美得别有风情，但缺失厚重、华而不实，两种制度下人格本质的区别、彻夜的华灯也渲染着异乡的漠然。恍然已记不清红烛昏罗帐的少年梦了，看过江阔云低，听罢断雁西风，也有了些游子的漂泊意味。如今追思，为什么选择香港？我想，天涯，也许不繁华，但走过追求这必经路，越过云端的眼睛不会沉溺于世俗，梦着自由的心灵不会陷没于囹圄。")])])}),[],!1,null,null,null);n.default=t.exports}}]);